CREATE OR REPLACE PROCEDURE gerar_view_med_resultado_exame (nr_seq_cliente_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_comando_w		varchar(32000) := '';
ds_decode_w		varchar(32000) := '';
ds_tipo_resultado_w	varchar(32000) := '';
dt_exame_w		timestamp;
cd_medico_w		varchar(10);

c01 CURSOR FOR
	SELECT	a.dt_exame
	from	med_result_exame a
	where	a.nr_seq_cliente = nr_seq_cliente_p
	group	by a.dt_exame
	order	by a.dt_exame desc;



BEGIN

select	cd_medico
into STRICT	cd_medico_w
from	med_cliente
where	nr_sequencia	= nr_seq_cliente_p;


open  c01;
loop
fetch	c01 into dt_exame_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

begin

ds_decode_w  :=  ds_decode_w || ' , max(decode(a.dt_exame, ' ||
          'to_date(' || chr(39) || to_char(dt_exame_w,'dd/mm/yyyy')
           || chr(39) || ',' || chr(39) || 'dd/mm/yyyy' || chr(39) ||
          ')' || ', decode(b.ie_formato_resultado, ' || chr(39) ||
          'D' || chr(39) ||  ',a.ds_valor_exame,
          substr(replace(campo_mascara(a.vl_exame,
          nvl(b.qt_decimais,0)), ' || chr(39) || '.' || chr(39) ||
          ',' || chr(39) || ',' || chr(39) || '),1,50)))) ' ||
                          'dia_' || to_char(dt_exame_w,'ddmmyyyy');

end;
end  loop;
close  c01;

CALL Exec_sql_dinamico('Tasy', ' drop view med_resultado_exame_' || nm_usuario_p || '_v');

ds_comando_w  :=  ' create or replace view med_resultado_exame_' || cd_medico_w || '_v as ' ||
         ' select a.nr_seq_exame, b.ds_exame Exame, ' ||
        ' b.ie_formato_resultado, b.nr_seq_apresent, obter_tipo_result_exame(a.nr_seq_exame, a.nr_seq_cliente) ds_tipo_resultado ' || ds_decode_w ||
        ' from med_exame_padrao b, med_result_exame a ' ||
        ' where a.nr_seq_exame   = b.nr_sequencia ' ||
        '   and a.nr_seq_cliente = ' || nr_seq_cliente_p ||
        ' group by b.ds_exame, a.nr_seq_exame, b.ie_formato_resultado, b.nr_seq_apresent, a.nr_seq_cliente ';


CALL Exec_sql_dinamico('Tasy', ds_comando_w);

end;
