CREATE OR REPLACE PROCEDURE gerar_vinculo_probl_sintoma ( nr_seq_receita_amb_p bigint, nr_seq_med_receita_p bigint, nr_seq_item_solic_p bigint, nr_seq_solic_exame_p bigint, ie_tipo_vinculo_p text, nr_sequencia_p bigint, ds_lista_sintomas_p text, ie_opcao_p text, nr_seq_proc_odont_p bigint, nr_seq_pac_medic_uso_p bigint default null) AS $body$
DECLARE


ds_lista_sintomas_w		varchar(1000);
nr_pos_virgula_w		integer;
nr_seq_problema_w		prescr_item_sintoma.nr_seq_problema%type;
nm_usuario_w			varchar(15);


BEGIN

if ((nr_seq_receita_amb_p IS NOT NULL AND nr_seq_receita_amb_p::text <> '') or
	(nr_seq_med_receita_p IS NOT NULL AND nr_seq_med_receita_p::text <> '') or
	(nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') or
	(ie_tipo_vinculo_p IS NOT NULL AND ie_tipo_vinculo_p::text <> '') or
	(nr_seq_item_solic_p IS NOT NULL AND nr_seq_item_solic_p::text <> '') or
	(nr_seq_solic_exame_p IS NOT NULL AND nr_seq_solic_exame_p::text <> '') or
	(nr_seq_proc_odont_p IS NOT NULL AND nr_seq_proc_odont_p::text <> '') or
	(nr_seq_pac_medic_uso_p IS NOT NULL AND nr_seq_pac_medic_uso_p::text <> '')) then
	
	if (ie_opcao_p = 'I') then
		if (ds_lista_sintomas_p IS NOT NULL AND ds_lista_sintomas_p::text <> '') then
			nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

			ds_lista_sintomas_w := ds_lista_sintomas_p;
			
			if (substr(ds_lista_sintomas_w, length(ds_lista_sintomas_w)) <> ',') then
				ds_lista_sintomas_w := ds_lista_sintomas_w ||',';
			end if;
			
			while(ds_lista_sintomas_w IS NOT NULL AND ds_lista_sintomas_w::text <> '') loop
				begin
							
					nr_pos_virgula_w := position(',' in ds_lista_sintomas_w);
					
					if (nr_pos_virgula_w <> 0) then
						nr_seq_problema_w		:= substr(ds_lista_sintomas_w, 1, (nr_pos_virgula_w - 1));
						ds_lista_sintomas_w	:= substr(ds_lista_sintomas_w, (nr_pos_virgula_w + 1), length(ds_lista_sintomas_w));
					end if;
					
					if (nr_seq_problema_w IS NOT NULL AND nr_seq_problema_w::text <> '') then
						
						if (ie_tipo_vinculo_p = 'EXM') then
							insert into soap_item_sintoma(
												nr_sequencia,
												dt_atualizacao, 
												nm_usuario, 
												dt_atualizacao_nrec, 
												nm_usuario_nrec, 
												nr_seq_item_solic, 
												nr_seq_solic_exame, 
												nr_seq_problema
												) values (
												nextval('soap_item_sintoma_seq'),
												clock_timestamp(),
												nm_usuario_w,
												clock_timestamp(),
												nm_usuario_w,
												nr_seq_item_solic_p,
												nr_seq_solic_exame_p,
												nr_seq_problema_w
											);						
						
						elsif (ie_tipo_vinculo_p = 'MED') then
							insert into soap_item_sintoma(
												nr_sequencia,
												dt_atualizacao, 
												nm_usuario, 
												dt_atualizacao_nrec, 
												nm_usuario_nrec, 
												nr_seq_receita_amb, 
												nr_seq_med_receita, 
												nr_seq_problema
												) values (
												nextval('soap_item_sintoma_seq'),
												clock_timestamp(),
												nm_usuario_w,
												clock_timestamp(),
												nm_usuario_w,
												nr_seq_receita_amb_p,
												nr_seq_med_receita_p,
												nr_seq_problema_w
											);
											
						elsif (ie_tipo_vinculo_p = 'ODO') then
							insert into soap_item_sintoma(
												nr_sequencia,
												dt_atualizacao, 
												nm_usuario, 
												dt_atualizacao_nrec, 
												nm_usuario_nrec, 
												nr_seq_odont_proc, 
												nr_seq_problema
												) values (
												nextval('soap_item_sintoma_seq'),
												clock_timestamp(),
												nm_usuario_w,
												clock_timestamp(),
												nm_usuario_w,
												nr_seq_proc_odont_p,
												nr_seq_problema_w
											);
											
						elsif (ie_tipo_vinculo_p = 'MEDICUSO') then
							insert into soap_item_sintoma(
												nr_sequencia,
												dt_atualizacao, 
												nm_usuario, 
												dt_atualizacao_nrec, 
												nm_usuario_nrec, 
												nr_seq_pac_medic_uso, 
												nr_seq_problema
												) values (
												nextval('soap_item_sintoma_seq'),
												clock_timestamp(),
												nm_usuario_w,
												clock_timestamp(),
												nm_usuario_w,
												nr_seq_pac_medic_uso_p,
												nr_seq_problema_w
											);
											
						end if;								
					end if;			
				end;
			end loop;
				
			commit;
		end if;
	elsif (ie_opcao_p = 'D') then
	
		delete
		from	soap_item_sintoma
		where	1 = 1
		and		nr_sequencia = nr_sequencia_p;
		commit;
		
	end if;
end if;

end;
$body$
