CREATE OR REPLACE PROCEDURE gerar_visitante_paciente ( nr_atendimento_p bigint, nm_usuario_p text, cd_pessoa_p INOUT text, nm_pessoa_p INOUT text, nr_identidade_p INOUT text) AS $body$
DECLARE


nr_sequencia_w			bigint;
cd_pessoa_w				varchar(10);
nm_pessoa_w				varchar(60);
nr_identidade_w			varchar(15);


BEGIN


select 	nextval('atendimento_visita_seq')
into STRICT	nr_sequencia_w
;

select 	a.cd_pessoa_fisica,
		substr(obter_nome_pf(a.cd_pessoa_fisica),1,60) nm_pessoa,
		a.nr_identidade
into STRICT	cd_pessoa_w,
		nm_pessoa_w,
		nr_identidade_w
from 	pessoa_fisica a
where 	a.cd_pessoa_fisica = (SELECT b.cd_pessoa_fisica from atendimento_paciente b where b.nr_atendimento = nr_atendimento_p);

insert into atendimento_visita(
	nr_sequencia,
	nr_atendimento,
	dt_atualizacao,
	nm_usuario,
	dt_entrada,
	nr_seq_tipo,
	dt_saida,
	ds_observacao,
	cd_pessoa_fisica,
	nr_seq_controle,
	nm_visitante,
	cd_setor_atendimento,
	nr_identidade,
	nr_telefone,
	dt_nascimento,
	ie_paciente)
values (nr_sequencia_w,
	nr_atendimento_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	null,
	null,
	null,
	cd_pessoa_w,
	null,
	nm_pessoa_w,
	null,
	nr_identidade_w,
	null,
	null,
	'S');
	
commit;

cd_pessoa_p := cd_pessoa_w;
nm_pessoa_p := nm_pessoa_w;
nr_identidade_p := nr_identidade_w;

END;
$body$
