CREATE OR REPLACE PROCEDURE gerar_vl_fat_gv ( nr_sequencia_p text) AS $body$
DECLARE


vl_total_w	double precision;


BEGIN
select	sum(a.vl_despesa)
into STRICT	vl_total_w
from	fin_faturamento_gv_vinc a
where	a.nr_seq_fat_gv = nr_sequencia_p;

update	fin_faturamento_gv
set	vl_total = vl_total_w
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
