SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_wpreco_amb_cbhpm ( cd_edicao_amb_p bigint, dt_vigencia_cbhpm_p timestamp) AS $body$
DECLARE

 
cd_proc_amb_w		bigint;
ie_origem_proc_amb_w	bigint;
vl_proc_amb_w		double precision;
cd_proc_cbhpm_w		bigint;
ie_origem_proc_cbhpm_w	bigint;
vl_proc_cbhpm_w		double precision;

C01 CURSOR FOR 
	SELECT	cd_procedimento, 
		ie_origem_proced, 
		vl_procedimento 
	from	preco_amb 
	where	cd_edicao_amb	= cd_edicao_amb_p 
	order by 1;


BEGIN 
 
delete	from w_amb_cbhpm;
commit;
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_proc_amb_w, 
	ie_origem_proc_amb_w, 
	vl_proc_amb_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	cd_proc_cbhpm_w	:= 0;
	begin 
	select	cd_procedimento, 
		ie_origem_proced, 
		obter_valor_preco_cbhpm(dt_vigencia_cbhpm_p, cd_procedimento, ie_origem_proced) 
	into STRICT	cd_proc_cbhpm_w, 
		ie_origem_proc_cbhpm_w, 
		vl_proc_cbhpm_w 
	from	cbhpm_preco 
	where	cd_procedimento		= cd_proc_amb_w 
	and	ie_origem_proced	= 5 
	and	dt_vigencia		=	(SELECT	max(x.dt_vigencia) 
						from 	cbhpm_preco x 
						where	x.cd_procedimento	= cd_proc_amb_w 
						and	x.ie_origem_proced	= 5 
						and	x.dt_vigencia		<= dt_vigencia_cbhpm_p) 
	order by 1;
	exception 
		when others then 
			cd_proc_cbhpm_w	:= 0;
		ie_origem_proc_cbhpm_w	:= 0;
			vl_proc_cbhpm_w	:= 0;
	end;
 
	if (cd_proc_cbhpm_w	> 0) then 
		insert into w_amb_cbhpm(NR_SEQUENCIA, 
			CD_PROC_AMB, 
			IE_ORIGEM_PROC_AMB, 
			VL_PROC_AMB, 
			CD_PROC_CBHPM, 
			IE_ORIGEM_PROC_CBHPM, 
			VL_PROC_CBHPM) 
		values (nextval('w_amb_cbhpm_seq'), 
			cd_proc_amb_w, 
			ie_origem_proc_amb_w, 
			vl_proc_amb_w, 
			cd_proc_cbhpm_w, 
			ie_origem_proc_cbhpm_w, 
			vl_proc_cbhpm_w);
	end if;
	end;
END LOOP;
CLOSE C01;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
