CREATE OR REPLACE PROCEDURE gerar_w_acrescimo_lote ( nr_seq_lote_protocolo_p bigint, vl_acrescimo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;



BEGIN

select	count(*)
into STRICT	qt_registro_w
from	w_acrescimo_lote
where	nr_seq_lote_protocolo = nr_seq_lote_protocolo_p;


if (qt_registro_w = 0) then

	if (vl_acrescimo_p > 0) then
		insert into w_acrescimo_lote(
			nr_seq_lote_protocolo,
			vl_acrescimo,
			dt_atualizacao,
			nm_usuario)
		values (	nr_seq_lote_protocolo_p,
			coalesce(vl_acrescimo_p,0),
			clock_timestamp(),
			nm_usuario_p);
	end if;
else
	update	w_acrescimo_lote
	set	vl_acrescimo = coalesce(vl_acrescimo_p,0)
	where	nr_seq_lote_protocolo = nr_seq_lote_protocolo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
