CREATE OR REPLACE PROCEDURE gerar_w_curva_abc_estoque ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_grupo_material_p bigint, dt_mesano_referencia_p timestamp) AS $body$
DECLARE



nr_sequencia_w		bigint;
vl_total_estoque_w		double precision;
cd_local_estoque_w	smallint;
cd_local_estoque_ww	smallint;
cd_material_w		integer;
qt_estoque_w		double precision;
vl_estoque_w		double precision;
vl_estoque_acum_w	double precision;
ie_curva_w		varchar(1);
pr_estoque_w		double precision;
pr_acumulado_w		double precision;
vl_curvaa_w		double precision;
vl_curvab_w		double precision;
vl_curvac_w		double precision;
cd_grupo_material_w	smallint;

c01 CURSOR FOR
SELECT	a.cd_local_estoque,
	a.cd_material,
	a.qt_estoque,
	a.vl_estoque
from	estrutura_material_v b,
	saldo_estoque a
where	a.cd_material		= b.cd_material
and	a.cd_estabelecimento	= cd_estabelecimento_p
and (a.cd_local_estoque	= cd_local_estoque_ww or cd_local_estoque_ww = 0)
and (b.cd_grupo_material	= cd_grupo_material_w or cd_grupo_material_w = 0)
and	a.dt_mesano_referencia	= dt_mesano_referencia_p
and	a.vl_estoque > 0
order by
	a.vl_estoque desc;


BEGIN

delete from w_curva_abc_estoque;
commit;

cd_local_estoque_ww	:= coalesce(cd_local_estoque_p, 0);
cd_grupo_material_w	:= coalesce(cd_grupo_material_p, 0);

vl_estoque_acum_w	:= 0;
pr_estoque_w		:= 0;
pr_acumulado_w		:= 0;


select	vl_curvaa,
	vl_curvab,
	vl_curvac
into STRICT	vl_curvaa_w,
	vl_curvab_w,
	vl_curvac_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

select	sum(a.vl_estoque)
into STRICT	vl_total_estoque_w
from	estrutura_material_v b,
	saldo_estoque a
where	a.cd_material		= b.cd_material
and	a.cd_estabelecimento	= cd_estabelecimento_p
and (a.cd_local_estoque	= cd_local_estoque_ww or cd_local_estoque_ww = 0)
and (b.cd_grupo_material	= cd_grupo_material_w or cd_grupo_material_w = 0)
and	a.dt_mesano_referencia	= dt_mesano_referencia_p
and	a.vl_estoque > 0;

open c01;
loop
fetch c01 into
	cd_local_estoque_w,
	cd_material_w,
	qt_estoque_w,
	vl_estoque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_sequencia), 0) + 1
	into STRICT	nr_sequencia_w
	from	w_curva_abc_estoque;

	vl_estoque_acum_w	:= vl_estoque_acum_w + vl_estoque_w;
	pr_estoque_w		:= dividir((vl_estoque_w * 100), vl_total_estoque_w);
	pr_acumulado_w		:= pr_acumulado_w + pr_estoque_w;


	if (pr_acumulado_w <= vl_curvaa_w) then
		ie_curva_w	:= 'A';
	elsif (pr_acumulado_w <= vl_curvaa_w + vl_curvab_w) then
		ie_curva_w	:= 'B';
	else
		ie_curva_w	:= 'C';
	end if;


	insert into w_curva_abc_estoque(
		nr_sequencia,
		cd_estabelecimento,
		cd_local_estoque,
		cd_material,
		dt_mesano_referencia,
		qt_estoque,
		vl_estoque,
		vl_acumulado,
		ie_curva,
		pr_estoque,
		pr_acumulado)
	values (	nr_sequencia_w,
		cd_estabelecimento_p,
		cd_local_estoque_w,
		cd_material_w,
		dt_mesano_referencia_p,
		qt_estoque_w,
		vl_estoque_w,
		vl_estoque_acum_w,
		ie_curva_w,
		pr_estoque_w,
		pr_acumulado_w);



	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
