CREATE OR REPLACE PROCEDURE gerar_w_dependencia_atend ( nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p text ) AS $body$
DECLARE


nm_tabela_w				varchar(50);
ds_tabela_w     				varchar(200);
qt_itens_w				bigint;
ds_sep_bv_w				varchar(10);

C01 CURSOR FOR
	SELECT	a.nm_tabela,
		b.ds_tabela
	from	tabela_sistema b,
		integridade_referencial a
	where	a.nm_tabela = b.nm_tabela
	and	a.nm_tabela_referencia = 'ATENDIMENTO_PACIENTE'
	order by a.nm_tabela;


BEGIN

select	obter_separador_bv
into STRICT	ds_sep_bv_w
;

CALL exec_sql_dinamico_bv('TASY','delete from w_dependencia_atend
                             where nm_usuario = :nm_usuario',
                             ':nm_usuario=' || nm_usuario_p);

open C01;
loop
fetch C01 into
	nm_tabela_w,
	ds_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

        	qt_itens_w := obter_valor_dinamico_bv(        'select count(*)
				from '|| nm_tabela_w ||
                                        		' where nr_atendimento = :nr_atendimento', 'nr_atendimento=' || nr_atendimento_p, qt_itens_w);
        	if (qt_itens_w > 0) then
		insert into w_dependencia_atend(
			nm_usuario,
			ds_dependencia,
			ds_tabela,
			qt_dependencia)
		values (
			nm_usuario_p,
			nm_tabela_w,
			ds_tabela_w,
			qt_itens_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
