CREATE OR REPLACE PROCEDURE gerar_w_dieta_consistencia ( nr_prescricao_p bigint, nr_seq_consistencia_p bigint, nr_seq_composicao_p bigint, nm_usuario_p text, ie_composicao_p text default null) AS $body$
DECLARE


nr_seq_w	bigint;


BEGIN

if	(nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_seq_consistencia_p IS NOT NULL AND nr_seq_consistencia_p::text <> '') then

	delete	FROM w_dieta_consistencia
	where	nm_usuario = nm_usuario_p
	and	((nr_seq_consistencia = nr_seq_consistencia_p) or (nr_seq_consistencia_p = 0))
	and	nr_seq_composicao_p = 0
	and	nr_prescricao = nr_prescricao_p;

	if (nr_prescricao_p 	<> 0) and (nr_seq_consistencia_p 	<> 0) and (nr_seq_composicao_p 	<> 0) and (coalesce(ie_composicao_p::text, '') = '') then

		select	nextval('w_dieta_consistencia_seq')
		into STRICT	nr_seq_w
		;

		insert into w_dieta_consistencia(nr_sequencia,
						 nr_prescricao,
						 nr_seq_consistencia,
						 nr_seq_composicao,
						 nm_usuario,
						 nm_usuario_nrec,
						 dt_atualizacao,
						 dt_atualizacao_nrec)
					values (nr_seq_w,
						 nr_prescricao_p,
						 nr_seq_consistencia_p,
						 nr_seq_composicao_p,
						 nm_usuario_p,
						 nm_usuario_p,
						 clock_timestamp(),
						 clock_timestamp());
	elsif (nr_prescricao_p 	> 0) and (nr_seq_consistencia_p 	> 0) and (ie_composicao_p = 'N') then

		select	nextval('w_dieta_consistencia_seq')
		into STRICT	nr_seq_w
		;

		insert into w_dieta_consistencia(
			nr_sequencia,
			nr_prescricao,
			nr_seq_consistencia,
			nr_seq_composicao,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec)
		values (nr_seq_w,
			nr_prescricao_p,
			nr_seq_consistencia_p,
			null,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp());
	end if;

end if;

commit;

end;
$body$
