CREATE OR REPLACE PROCEDURE gerar_w_etiq_mult_lote_fornec ( nr_seq_lote_fornec_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_relat_p bigint, ie_excluir_p text) AS $body$
DECLARE

 
qt_colunas_w		smallint;
nr_coluna_w		bigint;
nr_linha_w		bigint := 1;
nr_etiqueta_w		bigint;
nr_max_coluna_w		bigint;
nr_max_etiqueta_w	bigint;
nr_max_linha_w		bigint;
qt_material_w		bigint;
i			integer;
			

BEGIN 
 
if (ie_excluir_p = 'S') then 
	delete 
	from	w_etiqueta_lote_fornec 
	where	nm_usuario = nm_usuario_p 
	and	cd_estabelecimento = cd_estabelecimento_p;	
else 
	select	qt_coluna 
	into STRICT	qt_colunas_w 
	from	relatorio 
	where	nr_sequencia = nr_seq_relat_p;
 
	select 	max(nr_etiqueta) 
	into STRICT	nr_max_etiqueta_w 
	from	w_etiqueta_lote_fornec 
	where	nm_usuario = nm_usuario_p 
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	nr_seq_relatorio = nr_seq_relat_p;
	 
	begin 
	select	nr_coluna 
	into STRICT	nr_max_coluna_w 
	from	w_etiqueta_lote_fornec 
	where	nr_etiqueta = nr_max_etiqueta_w 
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	nr_seq_relatorio = nr_seq_relat_p 
	and	nm_usuario = nm_usuario_p;
	exception 
		when no_data_found then 
			nr_max_coluna_w := 0;
	end;
	 
	begin 
	select	nr_linha 
	into STRICT	nr_max_linha_w 
	from	w_etiqueta_lote_fornec 
	where	nr_etiqueta = nr_max_etiqueta_w 
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	nr_seq_relatorio = nr_seq_relat_p 
	and	nm_usuario = nm_usuario_p;
	exception 
		when no_data_found then 
			nr_max_linha_w := 0;
	end;
	 
	if (nr_max_coluna_w >= qt_colunas_w) then 
		nr_linha_w := coalesce(nr_max_linha_w,0) + 1;
	else 
		nr_linha_w := nr_max_linha_w;
	end if;
	 
	select	qt_material 
	into STRICT	qt_material_w 
	from	material_lote_fornec 
	where	nr_sequencia = nr_seq_lote_fornec_p;
	 
	if (nr_max_coluna_w >= qt_colunas_w) then 
		nr_coluna_w := 1;
	else 
		nr_coluna_w := coalesce(nr_max_coluna_w,0) + 1;
	end if;
	 
	nr_etiqueta_w := coalesce(nr_max_etiqueta_w,0) + 1;
 
	for i in 1..qt_material_w loop 
		begin 
		insert into 
			w_etiqueta_lote_fornec( 
			nr_sequencia, 
			nm_usuario, 
			nr_seq_lote_fornec, 
			nr_coluna, 
			nr_etiqueta, 
			nr_linha, 
			cd_estabelecimento, 
			nr_seq_relatorio, 
			dt_atualizacao) 
			values ( 
			nextval('w_etiqueta_lote_fornec_seq'), 
			nm_usuario_p, 
			nr_seq_lote_fornec_p, 
			nr_coluna_w, 
			nr_etiqueta_w, 
			nr_linha_w, 
			cd_estabelecimento_p, 
			nr_seq_relat_p, 
			clock_timestamp());
		 
		if (nr_coluna_w >= qt_colunas_w) then 
			begin 
			nr_coluna_w := 1;
			nr_linha_w := nr_linha_w + 1;
			end;
		else 
			nr_coluna_w := nr_coluna_w + 1;
		end if;
		 
		nr_etiqueta_w := nr_etiqueta_w + 1;
		end;
	end loop;
	 
	update 	material_lote_fornec 
    set	dt_impressao = clock_timestamp(), 
		nm_usuario_impressao = nm_usuario_p 
	where 	nr_sequencia = nr_seq_lote_fornec_p;
	 
	CALL gerar_material_lote_fornec_imp(nm_usuario_p, nr_seq_lote_fornec_p, qt_material_w, cd_estabelecimento_p);
end if;	
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
