SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_w_funcao_nao_cadastrada (nm_usuario_p text, ds_form_p text, ds_classe_p text, ds_nome_funcao_p text) AS $body$
DECLARE


/* ie_tipo_funcao_w

N	função não cadastrada
D	form cadastrado diferente do form real

*/
ds_nome_funcao_w	varchar(255);
ie_tipo_funcao_w	varchar(15);


BEGIN

ds_nome_funcao_w	:= upper(ds_nome_funcao_p);

select	coalesce(max('D'),'N')
into STRICT	ie_tipo_funcao_w
from	funcao a
where	upper(a.ds_funcao)	= ds_nome_funcao_w;

delete	from w_funcao_nao_cadastrada
where	ds_nome_funcao	= ds_nome_funcao_p
and	ds_classe	= ds_classe_p
and	ds_form		= ds_form_p
and	nm_usuario	= nm_usuario_p;

insert	into w_funcao_nao_cadastrada(ds_classe,
	ds_form,
	ds_nome_funcao,
	dt_atualizacao,
	dt_atualizacao_nrec,
	ie_tipo_funcao,
	nm_usuario,
	nm_usuario_nrec)
values (ds_classe_p,
	ds_form_p,
	ds_nome_funcao_p,
	clock_timestamp(),
	clock_timestamp(),
	ie_tipo_funcao_w,
	nm_usuario_p,
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
