SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_w_giro_estoque ( dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_local_estoque_w			smallint;
cd_material_w				integer;
qt_estoque_medio_w			double precision;
vl_custo_medio_w				double precision;
vl_estoque_w				double precision;
vl_consumo_w				double precision;
vl_estoque_medio_W			double precision;
vl_excedente_w				double precision := 0;
vl_estoque_calc_max_w			double precision := 0;
qt_estoque_geral_w			double precision := 0;
vl_estoque_geral_w				double precision := 0;
nr_sequencia_w				bigint;

/*Materiais com estoque*/

c01 CURSOR FOR
SELECT	cd_material
from	saldo_estoque
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p
group by cd_material
order by cd_material;

/*Locais de estoque e o saldo*/

c02 CURSOR FOR
SELECT	distinct
	cd_local_estoque,
	coalesce(qt_estoque_medio,0),
	coalesce(vl_estoque,0)
from	saldo_estoque
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p
and	cd_material		= cd_material_w;


BEGIN

delete	FROM w_giro_estoque
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p;

open c01;
loop
fetch c01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open c02;
	loop
	fetch c02 into
		cd_local_estoque_w,
		qt_estoque_medio_w,
		vl_estoque_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		select	round((obter_custo_medio_material(cd_estabelecimento_p, dt_mesano_referencia_p, cd_material_w))::numeric,4)
		into STRICT	vl_custo_medio_w
		;

		vl_estoque_medio_w		:= round((qt_estoque_medio_w * vl_custo_medio_w)::numeric, 2);

		select	sum(qt_estoque),
			sum(vl_estoque)
		into STRICT	qt_estoque_geral_w,
			vl_estoque_geral_w
		from	saldo_estoque
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	dt_mesano_referencia	= dt_mesano_referencia_p
		and	cd_material		= cd_material_w;


		select	sum(vl_consumo)
		into STRICT	vl_consumo_w
		from	movto_estoque_operacao_v
		where	dt_mesano_referencia = dt_mesano_referencia_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	cd_material = cd_material_w
		and	cd_local_estoque = cd_local_estoque_w;

		insert into w_giro_estoque(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			cd_estabelecimento,
			dt_mesano_referencia,
			cd_local_estoque,
			vl_estoque,
			vl_estoque_medio,
			vl_consumo,
			cd_material)
		values (	nextval('w_giro_estoque_seq'),
			clock_timestamp(),
			nm_usuario_p,
			cd_estabelecimento_p,
			dt_mesano_referencia_p,
			cd_local_estoque_w,
			vl_estoque_w,
			vl_estoque_medio_w,
			vl_consumo_w,
			cd_material_w);
		end;
	end loop;
	close c02;
end;
end loop;
close c01;
--end;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
