CREATE OR REPLACE PROCEDURE gerar_w_inventario_barra ( cd_estabelecimento_p bigint, nm_usuario_p text, cd_local_estoque_p bigint, cd_material_p bigint, nr_seq_lote_fornec_p bigint, cd_fornecedor_p text, qt_inventario_p bigint) AS $body$
DECLARE



cd_lote_fabricacao_w		varchar(20);
dt_validade_w			timestamp;
cd_material_estoque_w	integer;



BEGIN
if (coalesce(nr_seq_lote_fornec_p, 0) > 0) then
	select	ds_lote_fornec,
		dt_validade
	into STRICT	cd_lote_fabricacao_w,
		dt_validade_w
	from	material_lote_fornec
	where	nr_sequencia = nr_seq_lote_fornec_p;
end if;

select	cd_material_estoque
into STRICT	cd_material_estoque_w
from	material
where	cd_material = cd_material_p;

insert into w_inventario_barra(
	nr_sequencia,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	cd_local_estoque,
	cd_material_estoque,
	nr_seq_lote_fornec,
	cd_fornecedor,
	cd_lote_fabricacao,
	dt_validade,
	qt_inventario)
values ( nextval('w_inventario_barra_seq'),
	cd_estabelecimento_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_local_estoque_p,
	cd_material_estoque_w,
	nr_seq_lote_fornec_p,
	cd_fornecedor_p,
	cd_lote_fabricacao_w,
	dt_validade_w,
	qt_inventario_p);

commit;

end;
$body$
