CREATE OR REPLACE PROCEDURE gerar_w_ipasgo_dad_gerais_pr ( dt_mesano_referencia_p timestamp, cd_convenio_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_tipo_fatura_p bigint, qt_linha_arq_p INOUT bigint) AS $body$
DECLARE

 
 
