		dt_atualizacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec, 
		nr_linha, 
		tp_registro, 
		nr_matricula_prestador, 
		dt_mesano_referencia, 
		cd_versao_layout, 
		ds_linha, 
		nr_seq_tipo_fatura) 
	values (	nextval('w_ipasgo_dados_gerais_pr_seq'), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		qt_linha_arq_p, 
		0, 
		nr_matricula_prestador_w, 
		dt_mesano_referencia_p, 
