CREATE OR REPLACE PROCEDURE gerar_w_log_conf_item_prescr ( nr_prescricao_p bigint, cd_material_p bigint, ds_observacao_p text, nr_seq_lote_fornec_p bigint, nm_usuario_p text, ie_acao_p text) AS $body$
DECLARE


/*ie_acao_p
I - Inclusão do item na tabela de log
E - Exclusão do item da tabela de log
*/
qt_existe_w		bigint;
nr_sequencia_w		bigint;
nr_seq_lote_fornec_w	bigint;


BEGIN

nr_seq_lote_fornec_w		:= nr_seq_lote_fornec_p;
if (coalesce(nr_seq_lote_fornec_p,0) = 0) then
	nr_seq_lote_fornec_w 	:= null;
end if;

if (ie_acao_p = 'I') then
	insert into w_log_conf_item_prescr(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			cd_material,
			ds_observacao,
			nr_seq_lote_fornec)
		values (	nextval('w_log_conf_item_prescr_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			cd_material_p,
			ds_observacao_p,
			nr_seq_lote_fornec_w);

elsif (ie_acao_p = 'E') then
	select	count(*)
	into STRICT	qt_existe_w
	from	w_log_conf_item_prescr
	where	nr_prescricao 		= nr_prescricao_p
	and	cd_material		= cd_material_p
	and	coalesce(nr_seq_lote_fornec,0) 	= coalesce(nr_seq_lote_fornec_w, 0);

	if (qt_existe_w > 0) then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	w_log_conf_item_prescr
		where	nr_prescricao 		= nr_prescricao_p
		and	cd_material		= cd_material_p
		and	coalesce(nr_seq_lote_fornec,0) 	= coalesce(nr_seq_lote_fornec_w, 0);

		delete from w_log_conf_item_prescr
		where	nr_sequencia = nr_sequencia_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
