CREATE OR REPLACE PROCEDURE gerar_w_me_altera_pedido_item ( nr_seq_pedido_p bigint, codigoproduto_p text, prazoentrega_p text, quantidade_p bigint, valorunitario_p bigint, percentualdesconto_p bigint, percentualipi_p bigint, valornormal_p bigint, codigounidade_p text, observacao_p text, fabricante_p text, nm_usuario_p text, nr_seq_pedido_item_p INOUT text) AS $body$
DECLARE


nr_seq_me_altera_pedido_item_w	bigint;
prazoentrega_w					timestamp;
nr_ordem_compra_w				w_me_altera_pedido.numeropedidowpd%type;


BEGIN

prazoentrega_w:= to_date(substr(replace(prazoentrega_p,'-','/'),1,10),'yyyy-mm-dd');

select	max(a.numeropedidowpd)
into STRICT	nr_ordem_compra_w
from	w_me_altera_pedido a
where 	a.nr_sequencia = nr_seq_pedido_p;

CALL inserir_log_me('OC', substr(nr_ordem_compra_w||'@',1,position('@' in nr_ordem_compra_w||'@')-1), 'AVISO', 'ROC', (WHEB_MENSAGEM_PCK.get_texto(297536) || ' ' || nr_ordem_compra_w), '136', nm_usuario_p,'');
/* Alteração no valor item da ordem */

select	nextval('w_me_altera_pedido_item_seq')
into STRICT	nr_seq_me_altera_pedido_item_w
;

insert into w_me_altera_pedido_item(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_pedido,
	codigoproduto,
	prazoentrega,
	quantidade,
	valorunitario,
	percentualdesconto,
	percentualipi,
	valornormal,
	codigounidade,
	observacao,
	fabricante)
values (	nr_seq_me_altera_pedido_item_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_pedido_p,
	codigoproduto_p,
	prazoentrega_w,
	quantidade_p,
	valorunitario_p,
	percentualdesconto_p,
	percentualipi_p,
	valornormal_p,
	codigounidade_p,
	observacao_p,
	fabricante_p);

commit;

nr_seq_pedido_item_p:= nr_seq_me_altera_pedido_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
