CREATE OR REPLACE PROCEDURE gerar_w_me_cancela_ped_itens ( nr_seq_pedido_p bigint, cd_produto_p text, qt_produto_p bigint, ds_encerrado_p text, nm_usuario_p text, nr_seq_pedido_item_p INOUT bigint) AS $body$
DECLARE


nr_seq_me_canc_ped_itens_w	bigint;
nr_ordem_compra_w			w_me_cancela_pedido.numeropedidowpd%type;


BEGIN

select	max(a.nr_seq_pedido)/*max(a.numeropedidowpd)*/
into STRICT	nr_ordem_compra_w
from	w_me_cancela_pedido_itens a
where 	a.nr_sequencia = nr_seq_pedido_p;

CALL inserir_log_me('OC', substr(nr_ordem_compra_w||'@',1,position('@' in nr_ordem_compra_w||'@')-1), 'AVISO', 'ROC', (WHEB_MENSAGEM_PCK.get_texto(296106) || ' ' || nr_ordem_compra_w), '126', nm_usuario_p,'');
/* Cancelamento do item */

select	nextval('w_me_cancela_pedido_itens_seq')
into STRICT	nr_seq_me_canc_ped_itens_w
;

/*Tabela temporária usada na integração com o sistema Mercado Eletrônico.
Nesta tabela ficam registrados os itens dos pedido de compras que foram cancelados no sistema Mercado Eletrônico, e que precisam ser cancelados também no Tasy.*/
insert into w_me_cancela_pedido_itens(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_pedido,
	codigoproduto,
	quantidade,
	encerrado)
values (	nr_seq_me_canc_ped_itens_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_pedido_p,
	cd_produto_p,
	qt_produto_p,
	ds_encerrado_p);

commit;

nr_seq_pedido_item_p:= nr_seq_me_canc_ped_itens_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
