CREATE OR REPLACE PROCEDURE gerar_w_me_pedido_itens_ent ( nr_seq_item_p bigint, data_p text, quantidade_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_me_pedido_itens_ent_w	bigint;
data_w				timestamp;
quantidade_item_w		w_me_pedido_itens.quantidade%type;
nr_seq_pedido_w			w_me_pedido_itens.nr_seq_pedido%type;
nr_sequencia_w			w_me_pedido_itens.nr_sequencia%type;
quantidade_w			w_me_pedido_itens.quantidade%type;
quantidade_ww			w_me_pedido_itens.quantidade%type;
qt_fator_w			double precision;
codigoproduto_w			w_me_pedido_itens.codigoproduto%type;
nr_pedido_w       		w_me_pedido.numeropedidowpd%type;


c01 CURSOR FOR
	SELECT	nr_sequencia,
		quantidade
	from	w_me_pedido_itens
	where	nr_seq_pedido = nr_seq_pedido_w
	and	codigoproduto = codigoproduto_w
	order by 1 desc;


BEGIN
data_w := to_date(substr(replace(data_p,'-','/'),1,10),'yyyy-mm-dd');

select	nr_seq_pedido,
	codigoproduto
into STRICT	nr_seq_pedido_w,
	codigoproduto_w
from	w_me_pedido_itens
where	nr_sequencia = nr_seq_item_p;

select	sum(quantidade)
into STRICT	quantidade_item_w
from	w_me_pedido_itens
where	nr_seq_pedido = nr_seq_pedido_w;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	quantidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	qt_fator_w := quantidade_w / quantidade_item_w;
	quantidade_w := quantidade_p * qt_fator_w;
	quantidade_w := trunc(quantidade_w);

	select max(b.numeropedidowpd)
	into STRICT	nr_pedido_w
	from 	w_me_pedido_itens a,
			w_me_pedido b
	where 	b.nr_sequencia = a.nr_seq_pedido
	and 	a.nr_sequencia = nr_seq_item_p;

	CALL inserir_log_me('SC', substr(nr_pedido_w||'@',1,position('@' in nr_pedido_w||'@')-1), 'AVISO', 'ROC', (WHEB_MENSAGEM_PCK.get_texto(1057356) || ' ' || nr_pedido_w), '148', nm_usuario_p,'');
	/*Grava a sequência da entrega do item da ordem de compra. */

	select	nextval('w_me_pedido_itens_ent_seq')
	into STRICT	nr_seq_me_pedido_itens_ent_w
	;

	insert into w_me_pedido_itens_ent( /*Tabela temporária usada para gravar as informações das entregas dos itens da ordem de compra que o sistema Mercado Eletrônico envia para o Tasy através de uma integração.*/
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_item,
		data,
		quantidade)
	values (	nr_seq_me_pedido_itens_ent_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		data_w,
		quantidade_p);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
