CREATE OR REPLACE PROCEDURE gerar_w_ordem_compra_item_dev ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_nf_saida_p bigint, nr_seq_nf_entrada_p bigint, nr_item_nf_entrada_p bigint, qt_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_nf_saida_w	nota_fiscal_item.nr_item_nf%type;


BEGIN

select	nr_item_nf_saida
into STRICT	nr_item_nf_saida_w
from (SELECT	CASE WHEN a.nr_item_nf=nr_item_nf_entrada_p THEN 1  ELSE 2 END ,
		a.nr_item_nf nr_item_nf_saida
	from	nota_fiscal_item a
	where	a.nr_sequencia = nr_seq_nf_saida_p
	and	a.nr_ordem_compra = nr_ordem_compra_p
	and	a.nr_item_oci = nr_item_oci_p
	order by 1,2) alias0 LIMIT 1;

insert into w_ordem_compra_item_dev(
	NR_SEQUENCIA,
	DT_ATUALIZACAO,
	NM_USUARIO,
	DT_ATUALIZACAO_NREC,
	NM_USUARIO_NREC,
	NR_ORDEM_COMPRA,
	NR_ITEM_OCI,
	NR_SEQ_NF_SAIDA,
	NR_ITEM_NF_SAIDA,
	NR_SEQ_NF_ENTRADA,
	NR_ITEM_NF_ENTRADA,
	QT_MATERIAL)
values (nextval('w_ordem_compra_item_dev_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_ordem_compra_p,
	nr_item_oci_p,
	nr_seq_nf_saida_p,
	nr_item_nf_saida_w,
	nr_seq_nf_entrada_p,
	nr_item_nf_entrada_p,
	qt_material_p);

commit;

end;
$body$
