CREATE OR REPLACE PROCEDURE gerar_w_partograma_contr_graf ( nr_atendimento_p bigint, ie_exige_liberacao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_hora_w		integer;
ie_interv_contracoes_w	varchar(15);
nr_seq_grafico_w	bigint;
qt_reg_contracoes_w	bigint;
i			bigint;
dt_registro_w		timestamp;

C01 CURSOR FOR
	SELECT	ie_hora,
		ie_interv_contracoes,
		coalesce(qt_contracao,1),
		dt_registro
	from     partograma
	where    nr_atendimento = nr_atendimento_p
	and      ((ie_exige_liberacao_p = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
	and      coalesce(ie_situacao,'A') = 'A'
	and	(ie_interv_contracoes IS NOT NULL AND ie_interv_contracoes::text <> '')
	order by ie_hora, nr_sequencia;

BEGIN

delete from w_pep_partograma where nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	ie_hora_w,
	ie_interv_contracoes_w,
	qt_reg_contracoes_w,
	dt_registro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	for i in 1..qt_reg_contracoes_w loop
		begin

		select	coalesce(max(nr_seq_grafico),0) +1
		into STRICT	nr_seq_grafico_w
		from	w_pep_partograma
		where	nr_atendimento = nr_atendimento_p
		and	nm_usuario = nm_usuario_p
		and	ie_hora = ie_hora_w;

		insert into w_pep_partograma(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_hora,
			ie_interv_contracoes,
			nr_atendimento,
			nr_seq_grafico,
			dt_registro)
		values (
			nextval('w_pep_partograma_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_hora_w,
			ie_interv_contracoes_w,
			nr_atendimento_p,
			nr_seq_grafico_w,
			dt_registro_w);
		end;
	end loop;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
