CREATE OR REPLACE PROCEDURE gerar_w_pontualidade_pj ( cd_cnpj_p text, dt_inicio_p timestamp, dt_final_p timestamp, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_entrega_limite_w		timestamp;
dt_real_entrega_w		timestamp;
qt_real_entrega_w		double precision;
qt_pontual_w			double precision := 0;
qt_pontual_item_w		double precision := 0;
qt_prevista_entrega_w		double precision;
qt_total_w			double precision := 0;
qt_total_item_w			double precision := 0;
pr_pontualidade_w		double precision := 0;
pr_pontualidade_item_w		double precision := 0;

c01 CURSOR FOR
SELECT	coalesce(c.dt_entrega_limite,c.dt_prevista_entrega),
	c.dt_real_entrega,
	c.qt_real_entrega,
	c.qt_prevista_entrega
from	ordem_compra a,
	ordem_compra_item b,
	ordem_compra_item_entrega c,
	estrutura_material_v e
where	b.cd_material 		= e.cd_material
and	b.nr_item_oci 		= c.nr_item_oci
and	b.nr_ordem_compra 	= c.nr_ordem_compra
and	a.nr_ordem_compra 	= b.nr_ordem_compra
and	coalesce(c.dt_entrega_limite,c.dt_prevista_entrega) between dt_inicio_p and fim_dia(dt_final_p)
and	obter_se_ordem_possui_nota(a.nr_ordem_compra) > 0
and	a.cd_cgc_fornecedor = cd_cnpj_p
and	a.cd_estabelecimento = cd_estabelecimento_p
and	((cd_grupo_material_p = 0) or (e.cd_grupo_material = cd_grupo_material_p))
and	((cd_subgrupo_material_p = 0) or (e.cd_subgrupo_material = cd_subgrupo_material_p))
and	((cd_classe_material_p = 0) or (e.cd_classe_material = cd_classe_material_p))
and	((cd_material_p = 0) or (e.cd_material = cd_material_p));


BEGIN

delete from w_pontualidade_pj
where nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	dt_entrega_limite_w,
	dt_real_entrega_w,
	qt_real_entrega_w,
	qt_prevista_entrega_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (trunc(dt_real_entrega_w,'dd') <= trunc(dt_entrega_limite_w,'dd')) then
		qt_pontual_w := qt_pontual_w + qt_real_entrega_w;

		if (qt_pontual_w <> 0) then
			qt_pontual_item_w := qt_pontual_item_w + 1;
		end if;
	end if;

	qt_total_w	:= qt_total_w + qt_prevista_entrega_w;
	qt_total_item_w	:= qt_total_item_w + 1;

	end;
end loop;
close C01;

pr_pontualidade_w	:= round(dividir((qt_pontual_w * 100),qt_total_w));
pr_pontualidade_item_w	:= round(dividir((qt_pontual_item_w * 100),qt_total_item_w));

insert into w_pontualidade_pj(
	cd_cnpj,
	nm_usuario,
	qt_pontual,
	qt_pontual_item,
	qt_total,
	qt_total_item,
	pr_pontualidade,
	pr_pontualidade_item)
values (	cd_cnpj_p,
	nm_usuario_p,
	qt_pontual_w,
	qt_pontual_item_w,
	qt_total_w,
	qt_total_item_w,
	pr_pontualidade_w,
	pr_pontualidade_item_w);

commit;

end;
$body$
