SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_w_proj_analise_tempos ( ie_etapa_p text, ie_etapa_classif_p text, ie_funcoes_p text, ie_funcoes_classif_p text, nr_seq_projetos_p text, nr_seq_etapa_p text) AS $body$
DECLARE



nr_seq_etapa_w		bigint;
nr_seq_projeto_w	bigint;
qt_hora_real_w		bigint;
qt_hora_etapa_w		double precision;
qt_hora_prev_etapa_w	double precision;
nr_seq_projetos_w	varchar(255);

/*  Etapas do cronograma  */

c01 CURSOR FOR
SELECT	nr_sequencia
from	proj_etapa_cronograma
where	((obter_se_contido(nr_sequencia,nr_seq_etapa_p) = 'S') or (nr_seq_etapa_p = '0'));


/* Projetos do par√¢metro */

c02 CURSOR FOR
SELECT	nr_sequencia
from	proj_projeto a
where	obter_se_contido(a.nr_sequencia,nr_seq_projetos_w) = 'S';




BEGIN

delete from w_proj_analise_tempos;

nr_seq_projetos_w := substr(nr_seq_projetos_p,1,length(nr_seq_projetos_p)-1);

open	c01;
loop
fetch	c01 into
	nr_seq_etapa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open	c02;
	loop
	fetch	c02 into
		nr_seq_projeto_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin


		select	sum(coalesce(b.qt_hora_real,0)) qt_hora_real,
			sum(coalesce(b.qt_hora_prev,0))
		into STRICT	qt_hora_etapa_w,
			qt_hora_prev_etapa_w
		from	proj_cron_etapa b,
			proj_cronograma c
		where	b.nr_seq_cronograma = c.nr_sequencia
		and	c.nr_seq_proj = nr_seq_projeto_w
		and	b.nr_seq_etapa = nr_seq_etapa_w;

		insert into 	w_proj_analise_tempos(	nr_seq_etapa,
					nr_seq_proj,
					qt_hora_etapa,
					qt_hora_prev_etapa)
				values (	nr_seq_etapa_w,
					nr_seq_projeto_w,
					qt_hora_etapa_w,
					qt_hora_prev_etapa_w);

		commit;

		end;
	end loop;
	close c02;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
