CREATE OR REPLACE PROCEDURE gerar_w_proj_horas_consultoria ( dt_mesano_referencia_p timestamp, nr_seq_proj_p bigint) AS $body$
DECLARE


qt_semanal_w		bigint;
qt_semanas_w		bigint;
vl_retorno_w		bigint;
qt_cont_semanas_w	bigint;
dt_semana_w		timestamp;
qt_total_semana_w	bigint := 0;
dt_orcamento_w		timestamp;

c01 CURSOR FOR
	SELECT	coalesce(b.qt_profissional * b.qt_hora_sem,0) qt_semanal,
		qt_semana,
		dt_orcamento
	from	proj_orcamento a,
		proj_orc_prof b
	where	a.nr_sequencia = b.nr_seq_orc
	and	a.nr_seq_proj = nr_seq_proj_p;



BEGIN

delete from w_proj_horas_consultoria;

open c01;
loop
fetch c01 into
	qt_semanal_w,
	qt_semanas_w,
	dt_orcamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	qt_cont_semanas_w := 0;

	while(qt_cont_semanas_w <= qt_semanas_w) loop

		dt_semana_w		:= adicionar_semanas(dt_orcamento_w,qt_cont_semanas_w);
		qt_total_semana_w	:= qt_semanal_w;

		insert into w_proj_horas_consultoria(dt_orcamento,
							qt_horas)
						values (dt_semana_w,
							qt_total_semana_w);

		commit;
		qt_cont_semanas_w := qt_cont_semanas_w + 1;
		end loop;

	end;

end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
