CREATE OR REPLACE PROCEDURE gerar_w_rel_comp_forn_6_meses ( dt_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_entrada_saida_w		timestamp;
cd_cgc_emitente_w		varchar(14);
vl_total_nf_w			double precision	:= 0;
qt_item_nf_w			double precision	:= 0;
qt_mes_w			bigint 	:= 0;
qt_nf_w				bigint 	:= 0;

c01 CURSOR FOR
SELECT	distinct
	PKG_DATE_UTILS.start_of(a.dt_entrada_saida,'month',0) dt_entrada_saida
from	nota_fiscal a
where	a.ie_tipo_nota in ('ES','EN')
and	a.ie_situacao = '1'
and	(a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
and	a.cd_estabelecimento = cd_estabelecimento_p
and	PKG_DATE_UTILS.start_of(a.dt_entrada_saida,'month',0) between PKG_DATE_UTILS.ADD_MONTH(dt_referencia_p,-12,0) and dt_referencia_p
order by 1 desc;

c02 CURSOR FOR
SELECT	distinct
	cd_cgc_emitente,
	sum(vl_total_nota)
from	nota_fiscal a
where	a.ie_tipo_nota in ('ES','EN')
and	a.ie_situacao = '1'
and	(a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
and	a.cd_estabelecimento = cd_estabelecimento_p
and	PKG_DATE_UTILS.start_of(a.dt_entrada_saida,'month',0) = dt_entrada_saida_w
group by a.cd_cgc_emitente
order by 1 desc;


BEGIN

delete from w_rel_comp_forn_meses;

open C01;
loop
fetch C01 into
	dt_entrada_saida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_mes_w := qt_mes_w + 1;
	open C02;
	loop
	fetch C02 into
		cd_cgc_emitente_w,
		vl_total_nf_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	count(*)
		into STRICT	qt_nf_w
		from (	SELECT	count(*) qt_nf
			from	nota_fiscal a,
				nota_fiscal_item b
			where	a.nr_sequencia = b.nr_sequencia
			and	a.ie_tipo_nota in ('ES','EN')
			and	a.ie_situacao = '1'
			and	(a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
			and	a.cd_estabelecimento = cd_estabelecimento_p
			and	a.cd_cgc_emitente = cd_cgc_emitente_w
			and	PKG_DATE_UTILS.start_of(a.dt_entrada_saida,'month',0) = dt_entrada_saida_w) alias5;

		insert into w_rel_comp_forn_meses(
			dt_atualizacao,
			nm_usuario,
			dt_referencia,
			cd_cgc_emitente,
			vl_total_nf,
			qt_mes)
		values (	clock_timestamp(),
			nm_usuario_p,
			dt_referencia_p,
			cd_cgc_emitente_w,
			vl_total_nf_w,
			qt_mes_w);
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;
commit;
end;
$body$
