SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_w_rel_grau_satisf_geral ( dt_referencia_p timestamp, nr_seq_grupo_p bigint, nr_seq_gerencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_gerencia_w			bigint;
nr_seq_gerencia_insatisf_w		bigint;
nr_seq_grupo_w			bigint;
ie_grau_satisfacao_w		varchar(10);
nr_sequencia_w			bigint;
nm_usuario_exec_w		varchar(15);
dt_fim_real_w			timestamp;


C01 CURSOR FOR /* Desenvolvimento */
	SELECT	a.nr_sequencia,
		a.nr_seq_grupo_des,
		a.ie_grau_satisfacao,
		dt_fim_real,
		a.nr_seq_gerencia_insatisf
	from	man_ordem_servico a
	where	PKG_DATE_UTILS.start_of(a.dt_fim_real,'month',0) between PKG_DATE_UTILS.ADD_MONTH(PKG_DATE_UTILS.start_of(dt_referencia_p,'month',0),-12,0) and PKG_DATE_UTILS.start_of(dt_referencia_p,'month',0)
	and	(a.ie_grau_satisfacao IS NOT NULL AND a.ie_grau_satisfacao::text <> '')
	and	(a.nr_seq_grupo_des IS NOT NULL AND a.nr_seq_grupo_des::text <> '')
	and exists (	SELECT	1
			from 	man_estagio_processo c,
				man_ordem_serv_estagio b
			where	b.nr_seq_estagio		= c.nr_sequencia
			and	c.ie_desenv		= 'S'
			and	a.nr_sequencia		= b.nr_seq_ordem);

C02 CURSOR FOR /* Suporte */
	SELECT	a.nr_sequencia,
		a.nr_seq_grupo_sup,
		a.ie_grau_satisfacao,
		dt_fim_real,
		a.nr_seq_gerencia_insatisf
	from	man_ordem_servico a
	where	PKG_DATE_UTILS.start_of(a.dt_fim_real,'month',0) between PKG_DATE_UTILS.ADD_MONTH(PKG_DATE_UTILS.start_of(dt_referencia_p,'month',0),-12,0) and PKG_DATE_UTILS.start_of(dt_referencia_p,'month',0)
	and	(a.ie_grau_satisfacao IS NOT NULL AND a.ie_grau_satisfacao::text <> '')
	and	(a.nr_seq_grupo_sup IS NOT NULL AND a.nr_seq_grupo_sup::text <> '')
	and 	not exists (	SELECT	1
			from 	man_estagio_processo c,
				man_ordem_serv_estagio b
			where	b.nr_seq_estagio		= c.nr_sequencia
			and	c.ie_desenv		= 'S'
			and	a.nr_sequencia		= b.nr_seq_ordem);


BEGIN

delete 	FROM w_rel_grau_satisf_des
where	nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_grupo_w,
	ie_grau_satisfacao_w,
	dt_fim_real_w,
	nr_seq_gerencia_insatisf_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(a.nm_usuario_exec)
	into STRICT	nm_usuario_exec_w
	from	man_ordem_servico_exec a
	where	nr_seq_ordem	= nr_sequencia_w
	and exists (	SELECT	1
			from	usuario_grupo_des b
			where	a.nm_usuario_exec = b.nm_usuario_grupo);

	select	max(nr_seq_gerencia)
	into STRICT	nr_seq_gerencia_w
	from	grupo_desenvolvimento
	where	nr_sequencia	= nr_seq_grupo_w;

	insert into w_rel_grau_satisf_des(
		nr_seq_ordem,
		nr_seq_grupo_des,
		ie_grau_satisfacao,
		nr_seq_gerencia,
		nm_usuario,
		nm_usuario_exec,
		ie_desenvolvimento,
		dt_fim_real)
	values (	nr_sequencia_w,
		nr_seq_grupo_w,
		ie_grau_satisfacao_w,
		coalesce(nr_seq_gerencia_insatisf_w, nr_seq_gerencia_w),
		nm_usuario_p,
		nm_usuario_exec_w,
		'S',
		dt_fim_real_w);

	end;
end loop;
close C01;

commit;

open C02;
loop
fetch C02 into
	nr_sequencia_w,
	nr_seq_grupo_w,
	ie_grau_satisfacao_w,
	dt_fim_real_w,
	nr_seq_gerencia_insatisf_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	select	max(a.nm_usuario_exec)
	into STRICT	nm_usuario_exec_w
	from	man_ordem_servico_exec a
	where	nr_seq_ordem	= nr_sequencia_w
	and exists (	SELECT	1
			from	usuario_grupo_sup b
			where	a.nm_usuario_exec = b.nm_usuario_grupo);

	select	max(nr_seq_gerencia_sup)
	into STRICT	nr_seq_gerencia_w
	from	grupo_suporte
	where	nr_sequencia	= nr_seq_grupo_w;

	insert into w_rel_grau_satisf_des(
		nr_seq_ordem,
		nr_seq_grupo_des,
		ie_grau_satisfacao,
		nr_seq_gerencia,
		nm_usuario,
		nm_usuario_exec,
		ie_desenvolvimento,
		dt_fim_real)
	values (	nr_sequencia_w,
		nr_seq_grupo_w,
		ie_grau_satisfacao_w,
		coalesce(nr_seq_gerencia_insatisf_w, nr_seq_gerencia_w),
		nm_usuario_p,
		nm_usuario_exec_w,
		'N',
		dt_fim_real_w);

	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
