CREATE OR REPLACE PROCEDURE gerar_w_rel_grau_satisf_sup ( dt_inicio_p text, dt_final_p text, nr_seq_grupo_p bigint, nr_seq_gerencia_p bigint, ie_encerradas_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_gerencia_w	bigint;
nr_seq_grupo_w		bigint;
ie_grau_satisfacao_w	varchar(10);
nr_sequencia_w		bigint;
nm_usuario_exec_w	varchar(15);


C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_grupo_sup,
		a.ie_grau_satisfacao
	from	man_ordem_servico a
	where	a.dt_fim_real between dt_inicio_p and dt_final_p
	and	(a.ie_grau_satisfacao IS NOT NULL AND a.ie_grau_satisfacao::text <> '')
	and	ie_encerradas_p = 'S'
	and	(a.nr_seq_grupo_sup IS NOT NULL AND a.nr_seq_grupo_sup::text <> '')
	and 	not exists (	SELECT	1
			from 	man_estagio_processo c,
				man_ordem_serv_estagio b
			where	b.nr_seq_estagio	= c.nr_sequencia
			and	c.ie_desenv	= 'S'
			and	a.nr_sequencia	= b.nr_seq_ordem)
	
union all

	select	a.nr_sequencia,
		a.nr_seq_grupo_sup,
		a.ie_grau_satisfacao
	from	man_ordem_servico a
	where	a.dt_ordem_servico between dt_inicio_p and dt_final_p
	and	(a.ie_grau_satisfacao IS NOT NULL AND a.ie_grau_satisfacao::text <> '')
	and	ie_encerradas_p = 'N'
	and	(a.nr_seq_grupo_sup IS NOT NULL AND a.nr_seq_grupo_sup::text <> '')
	and 	not exists (	select	1
			from 	man_estagio_processo c,
				man_ordem_serv_estagio b
			where	b.nr_seq_estagio	= c.nr_sequencia
			and	c.ie_desenv	= 'S'
			and	a.nr_sequencia	= b.nr_seq_ordem);


BEGIN

delete 	FROM w_rel_grau_satisf_des
where	nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_grupo_w,
	ie_grau_satisfacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(a.nm_usuario_exec)
	into STRICT	nm_usuario_exec_w
	from	man_ordem_servico_exec a
	where	nr_seq_ordem	= nr_sequencia_w
	and exists (	SELECT	1
			from	usuario_grupo_sup b
			where	a.nm_usuario_exec = b.nm_usuario_grupo);

	select	max(nr_seq_gerencia_sup)
	into STRICT	nr_seq_gerencia_w
	from	grupo_suporte
	where	nr_sequencia	= nr_seq_grupo_w;

	insert into w_rel_grau_satisf_des(
		nr_seq_ordem,
		nr_seq_grupo_des,
		ie_grau_satisfacao,
		nr_seq_gerencia,
		nm_usuario,
		nm_usuario_exec)
	values (	nr_sequencia_w,
		nr_seq_grupo_w,
		ie_grau_satisfacao_w,
		nr_seq_gerencia_w,
		nm_usuario_p,
		nm_usuario_exec_w);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_w_rel_grau_satisf_sup ( dt_inicio_p text, dt_final_p text, nr_seq_grupo_p bigint, nr_seq_gerencia_p bigint, ie_encerradas_p text, nm_usuario_p text) FROM PUBLIC;

