CREATE OR REPLACE PROCEDURE gerar_w_rel_indice ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_sequencia_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_apres_w		bigint;
ds_informacao_w		varchar(255);
ie_origem_valor_w		varchar(15);
ds_origem_w		varchar(4000);
nr_seq_inf_w		bigint;
vl_resultado_w		double precision;

c01 CURSOR FOR
SELECT	nr_seq_apres,
	ds_informacao,
	ie_origem_valor,
	ds_origem,
	nr_sequencia
from	indice_hospital_inf
where	nr_seq_indice = nr_sequencia_p;


BEGIN

delete	FROM w_rel_indice
where	nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	nr_seq_apres_w,
	ds_informacao_w,
	ie_origem_valor_w,
	ds_origem_w,
	nr_seq_inf_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	vl_resultado_w := 0;

	if (ie_origem_valor_w = 'SQL') then
		vl_resultado_w := ctb_calcular_indice_sql(cd_empresa_p, cd_estabelecimento_p, nr_seq_inf_w, dt_referencia_p, vl_resultado_w);
	elsif (ie_origem_valor_w = 'FI') then
		vl_resultado_w	:= somente_numero(ds_origem_w);
	end if;

	insert into w_rel_indice(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_apres,
		ds_informacao,
		nr_seq_inf,
		vl_resultado)
	values (	nextval('w_rel_indice_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		ds_informacao_w,
		nr_seq_inf_w,
		vl_resultado_w);

	end;
end loop;
close C01;

commit;

end;
$body$
