CREATE OR REPLACE PROCEDURE gerar_w_tit_pag_imposto (nr_titulo_p bigint, nm_usuario_p text, ie_baixa_titulo_p text default null) AS $body$
DECLARE


vl_saldo_titulo_w	double precision;
cd_estabelecimento_w	bigint;
ie_gerar_trib_temp_w	varchar(255);
ie_baixa_titulo_w	varchar(1);


BEGIN

select	max(vl_saldo_titulo),
	max(cd_estabelecimento)
into STRICT	vl_saldo_titulo_w,
	cd_estabelecimento_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

select	coalesce(max(ie_gerar_trib_temp), 'N')
into STRICT	ie_gerar_trib_temp_w
from	parametros_contas_pagar
where	cd_estabelecimento	= cd_estabelecimento_w;

if (coalesce(ie_baixa_titulo_p::text, '') = '') then
	ie_baixa_titulo_w := 'S';
else
	ie_baixa_titulo_w := 'N';
end if;

if (vl_saldo_titulo_w > 0) and (ie_gerar_trib_temp_w = 'S') then
	begin
	CALL gerar_tributo_titulo(nr_titulo_p,
		nm_usuario_p,
		ie_baixa_titulo_w,
		null,
		null,
		null,
		null,
		null,
		cd_estabelecimento_w,
		vl_saldo_titulo_w);
	exception
	when others then
		null;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
