CREATE OR REPLACE PROCEDURE gera_agenda_equip_disp ( nr_seq_agenda_p bigint, lista_equip_p INOUT text) AS $body$
DECLARE


cd_equipamento_w	bigint;
lista_equip_w		varchar(2000) := '';

c01 CURSOR FOR
	SELECT	distinct(cd_equipamento) cd_equipamento
	from	consistencia_agenda_cir
	where	nr_seq_agenda = nr_seq_agenda_p;


BEGIN
open c01;
loop
--fetch - buscar/trazer | into - em | ou seja trazer para
fetch c01 into cd_equipamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */ --sair quando c01 não tiver mais dados
	begin
	lista_equip_w := lista_equip_w || cd_equipamento_w || ',';
	end;--do begin após exit when
end loop;
close c01;

lista_equip_p	:= lista_equip_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
