SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gera_alerta_pj_hist () AS $body$
DECLARE



nm_usuario_w		pessoa_juridica.nm_usuario%type;
nr_sequencia_w 		comunic_interna.nr_sequencia%type;
ds_razao_social_w	pessoa_juridica.ds_razao_social%type;
cd_cgc_w		pessoa_juridica.cd_cgc%type;
ds_mensagem_w		varchar(200);


C01 CURSOR FOR
	SELECT	a.nm_usuario,
		b.ds_razao_social,
		a.cd_cgc
	from	pessoa_juridica_historico a,
		pessoa_juridica b
	where	a.cd_cgc = b.cd_cgc
	and	trunc(a.dt_revisar) = trunc(clock_timestamp())
	order by nm_usuario;


BEGIN

open C01;
loop
fetch C01 into	
	nm_usuario_w,
	ds_razao_social_w,
	cd_cgc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select	nextval('comunic_interna_seq')
	into STRICT	nr_sequencia_w
	;
	
	ds_mensagem_w := wheb_mensagem_pck.get_texto(807480,'DS_RAZAO_SOCIAL_W='||ds_razao_social_w ||';CD_CGC_W='||cd_cgc_w);
	
	insert into comunic_interna(
		dt_comunicado,
		ds_titulo,
		ds_comunicado,
		nm_usuario,
		dt_atualizacao,
		ie_geral,
		nm_usuario_destino,
		cd_perfil,
		nr_sequencia,
		ie_gerencial,
		nr_seq_classif,
		ds_perfil_adicional,
		cd_setor_destino,
		cd_estab_destino,
		ds_setor_adicional,
		dt_liberacao,
		ds_grupo)
	values (
		clock_timestamp(),
		wheb_mensagem_pck.get_texto(807481),
		ds_mensagem_w,
		'Tasy',
		clock_timestamp(),
		'N',
		nm_usuario_w,
		null,
		nr_sequencia_w,
		'N',
		null,
		null,
		null,
		null,
		null,
		clock_timestamp(),
		null);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
