CREATE OR REPLACE PROCEDURE gera_comp_kit_barra_avulso ( cd_material_p bigint, nr_seq_reg_kit_p bigint, qt_material_p bigint, nm_usuario_p text, nr_seq_lote_fornec_p bigint, cd_local_estoque_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_kit_material_w		integer;
ds_erro_w		varchar(255);


BEGIN

if (cd_material_p > 0) then

	select nextval('kit_estoque_comp_avulso_seq')
	into STRICT	nr_sequencia_w
	;

	insert into kit_estoque_comp_avulso(
		nr_sequencia,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		qt_material,
		ie_gerado_barras,
		nr_seq_lote_fornec,
		nr_seq_reg_kit,
		cd_local_estoque)
	values (	nr_sequencia_w,
		cd_material_p,
		clock_timestamp(),
		nm_usuario_p,
		qt_material_p,
		'S',
		CASE WHEN coalesce(nr_seq_lote_fornec_p,'0')='0' THEN  null  ELSE nr_seq_lote_fornec_p END ,
		nr_seq_reg_kit_p,
		cd_local_estoque_p);
commit;
end if;

ds_erro_p	:= substr(ds_erro_w,1,255);

end;
$body$
