CREATE OR REPLACE PROCEDURE gera_dados_ops_agecons (cd_estabelecimento_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text, carteiras_selec_p INOUT text) AS $body$
DECLARE


cd_usuario_plano_w	varchar(255);
dt_validade_w		timestamp;
cd_pessoa_fisica_w	varchar(10);
dt_agenda_w			timestamp;
atualiza_plano_w	varchar(1);
atualiza_codigo_w	varchar(1);
cd_plano_w			varchar(10);
cd_convenio_w		integer;
cd_categoria_w		varchar(10);


BEGIN

select  dt_agenda,
		cd_pessoa_fisica,
		cd_convenio,
		cd_categoria
into STRICT	dt_agenda_w,
		cd_pessoa_fisica_w,
		cd_convenio_w,
		cd_categoria_w
from	agenda_consulta
where	nr_sequencia = nr_seq_agenda_p;

atualiza_plano_w := Obter_Param_Usuario(821, 118, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, atualiza_plano_w);
atualiza_codigo_w := Obter_Param_Usuario(821, 117, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, atualiza_codigo_w);

if (atualiza_codigo_w = 'S') then

	SELECT * FROM pls_obter_usuario_plano(cd_pessoa_fisica_w, dt_agenda_w, cd_estabelecimento_p, cd_usuario_plano_w, dt_validade_w) INTO STRICT cd_usuario_plano_w, dt_validade_w;

	if (coalesce(cd_usuario_plano_w,'X') <> 'X') and (position(',' in cd_usuario_plano_w) = 0) then

		update	agenda_consulta
		set	cd_usuario_convenio	= cd_usuario_plano_w,
			dt_validade_carteira	= dt_validade_w
		where	nr_sequencia		= nr_seq_agenda_p;

		select	null
		into STRICT	carteiras_selec_p
		;

	else

		select	cd_usuario_plano_w
		into STRICT	carteiras_selec_p
		;

	end if;
end if;

if (atualiza_plano_w = 'S') then

	select	cd_usuario_convenio
	into STRICT	cd_usuario_plano_w
	from	agenda_consulta
	where	nr_sequencia		= nr_seq_agenda_p;

	select  max(cd_plano_padrao)
	into STRICT	cd_plano_w
	from	categoria_convenio
	where	cd_convenio = cd_convenio_w
	and		cd_categoria = cd_categoria_w;

	if (cd_plano_w = '') then
		select  obter_plano_cod_usuario_conv(cd_convenio_w, cd_usuario_plano_w)
		into STRICT	cd_plano_w
		;
	end if;

	if (cd_plano_w <> '') then
		update	agenda_consulta
		set		cd_plano	= cd_plano_w
		where	nr_sequencia	= nr_seq_agenda_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
