CREATE OR REPLACE PROCEDURE gera_estrut_regra_media_compra () AS $body$
DECLARE


nr_sequencia_w				bigint;
cd_grupo_material_w			bigint;
cd_subgrupo_material_w			bigint;
cd_classe_material_w			bigint;
qt_registros_w				bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
from	sup_media_compra_aprov;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	sup_media_compra_aprov_es
	where	nr_seq_regra = nr_sequencia_w;

	if (qt_registros_w = 0) then
		insert into sup_media_compra_aprov_es(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_regra,
			cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material)
		values (	nextval('sup_media_compra_aprov_es_seq'),
			clock_timestamp(),
			'Tasy',
			clock_timestamp(),
			'Tasy',
			nr_sequencia_w,
			cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w);

	end if;


	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
