CREATE OR REPLACE PROCEDURE gera_etapa_ap_lote_controle ( nr_sequencia_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_apres_ant_w		integer;
nr_seq_regra_w			bigint;
nr_seq_lote_w			bigint;
nr_sequencia_w			bigint;
ds_erro_w			varchar(255) := '';


BEGIN

update	ap_lote_controle_receb
set	dt_recebimento = clock_timestamp(),
	nm_usuario_receb = nm_usuario_p
where	nr_sequencia = nr_sequencia_p;

select	coalesce(max(nr_seq_apres),0)
into STRICT	nr_seq_apres_ant_w
from	ap_lote_controle_receb
where	nr_sequencia = nr_sequencia_p;

select	coalesce(min(nr_sequencia),0)
into STRICT	nr_seq_regra_w
from	regra_controle_receb
where	nr_seq_apres > nr_seq_apres_ant_w
order by nr_seq_apres;

if (nr_seq_regra_w > 0) then
	begin

	select	nr_seq_lote
	into STRICT	nr_seq_lote_w
	from	ap_lote_controle_receb
	where	nr_sequencia = nr_sequencia_p;

	select	nextval('ap_lote_controle_receb_seq')
	into STRICT	nr_sequencia_w
	;

	insert into ap_lote_controle_receb(
		nr_sequencia,
		nr_seq_lote,
		nr_seq_apres,
		nm_usuario_nrec,
		nm_usuario,
		dt_atualizacao_nrec,
		dt_atualizacao)
	values (	nr_sequencia_w,
		nr_seq_lote_w,
		nr_seq_regra_w,
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp());

	end;
else

	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(279329);
end if;

ds_erro_p := substr(ds_erro_w,1,254);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
