SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gera_etapa_prod_seperacao (dt_etapa_p timestamp, nr_seq_ordem_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		far_etapa_producao.nr_sequencia%type;
nr_seq_cabine_w		bigint;
cd_estab_destino_w 	far_etapa_producao.cd_estab_destino%type;
nr_prescricao_w		can_ordem_prod.nr_prescricao%type;



BEGIN

nr_seq_cabine_w := Obter_Param_Usuario(3130, 301, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_cabine_w);

if (coalesce(nr_seq_cabine_w,0) > 0) then


	select max(nr_prescricao)
	into STRICT   nr_prescricao_w
	from   can_ordem_prod
	where  nr_sequencia = nr_seq_ordem_p;


	select max(cd_estabelecimento)
	into STRICT   cd_estab_destino_w
	from   prescr_medica
	where  nr_prescricao = nr_prescricao_w;

	select	nextval('far_etapa_producao_seq')
	into STRICT	nr_sequencia_w
	;

	insert into far_etapa_producao(
		nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_etapa,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_cabine,
		cd_estab_destino)
	values (nr_sequencia_w,
		cd_estabelecimento_p,
		clock_timestamp(),
		clock_timestamp(),
		dt_etapa_p,
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_cabine_w,
		cd_estab_destino_w);

	update 	can_ordem_prod
	set 	nr_seq_etapa_prod 	= nr_sequencia_w,
		ie_conferido		= 'S',
		dt_fim_separacao = clock_timestamp()
	where  	trunc(dt_prevista) 	= trunc(dt_etapa_p)
	and    	nr_sequencia 		= nr_seq_ordem_p

	and 	coalesce(nr_seq_etapa_prod::text, '') = '';
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(173464);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
