CREATE OR REPLACE PROCEDURE gera_fleury_prescr_proced_val (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_possui_pergunta_tecnica_p text, cd_produto_fleury_p bigint, ie_pergunta_finalizada_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_prescr_proced_valid_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_prescr_proced_valid_w
from	fleury_prescr_proced_valid
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_prescr	= nr_seq_prescr_p;

if	not(nr_seq_prescr_proced_valid_w > 0) then

	insert into fleury_prescr_proced_valid(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_possui_pergunta_tecnica,
		cd_produto_fleury,
		nr_prescricao,
		nr_seq_prescr,
		ie_pergunta_finalizada
		)
	values (
		nextval('fleury_prescr_proced_valid_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_possui_pergunta_tecnica_p,
		cd_produto_fleury_p,
		nr_prescricao_p,
		nr_seq_prescr_p,
		ie_pergunta_finalizada_p
		);

else

	update	fleury_prescr_proced_valid
	set	ie_possui_pergunta_tecnica 	= ie_possui_pergunta_tecnica_p,
		cd_produto_fleury 	= cd_produto_fleury_p,
		ie_pergunta_finalizada	= ie_pergunta_finalizada_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p
	where	nr_sequencia	= nr_seq_prescr_proced_valid_w;

end if;

commit;

end;
$body$
