CREATE OR REPLACE PROCEDURE gera_inspecao_aprov_ordem ( nr_ordem_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_forma_registro_w		varchar(1);
nr_sequencia_w			bigint;
nr_seq_registro_w			bigint;
cd_estabelecimento_w		integer;
cd_cgc_fornecedor_w		varchar(14);
cd_pessoa_fisica_w		varchar(10);
ds_erro_w			varchar(255);

c01 CURSOR FOR 
SELECT	b.nr_sequencia 
from	ordem_compra_item_entrega b, 
	ordem_compra_item a 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_ordem_compra = nr_ordem_compra_p 
and	a.nr_item_oci = b.nr_item_oci 
and (b.qt_prevista_entrega - obter_qt_inspecao(a.nr_ordem_compra,a.nr_item_oci,b.dt_prevista_entrega)) > 0 
and	coalesce(b.qt_prevista_entrega,0) > coalesce(b.qt_real_entrega,0) 
and  	((coalesce(b.dt_inicio_pendencia::text, '') = '') or 
	(b.dt_inicio_pendencia IS NOT NULL AND b.dt_inicio_pendencia::text <> '' AND b.dt_fim_pendencia IS NOT NULL AND b.dt_fim_pendencia::text <> '')) 
and	coalesce(b.dt_cancelamento::text, '') = '';


BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
ie_forma_registro_w := obter_param_usuario(270, 4, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_forma_registro_w);
 
if (ie_forma_registro_w = 'S') then 
	begin 
 
	select	cd_cgc_fornecedor, 
		cd_pessoa_fisica, 
		nextval('inspecao_registro_seq') 
	into STRICT	cd_cgc_fornecedor_w, 
		cd_pessoa_fisica_w, 
		nr_seq_registro_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_p;
 
	insert into inspecao_registro( 
		nr_sequencia, 
		cd_estabelecimento,		 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		dt_atualizacao, 
		nm_usuario, 
		cd_cnpj, 
		cd_pessoa_fisica, 
		dt_registro, 
		ie_origem) values ( 
			nr_seq_registro_w, 
			cd_estabelecimento_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_cgc_fornecedor_w, 
			cd_pessoa_fisica_w, 
			clock_timestamp(), 
			'AO');
 
	end;
else 
	nr_seq_registro_w	:= 0;
end if;
 
open c01;
loop 
fetch c01 into 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	ds_erro_w := gerar_inspecao_ordem( 
		nr_sequencia_w, nm_usuario_p, '', nr_seq_registro_w, ds_erro_w);
 
	end;
end loop;
close c01;
 
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
