CREATE OR REPLACE PROCEDURE gera_pf_codigo_externo_gpl ( cd_pessoa_fisica_p text, cd_pessoa_fisica_externo_p text, cd_usuario_convenio_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_externo_w	varchar(20);
cd_usuario_convenio_w		varchar(255);
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type;
				

BEGIN

select wheb_usuario_pck.get_cd_estabelecimento
into STRICT cd_estabelecimento_w
;

select	max(cd_pessoa_fisica_externo),
	max(cd_usuario_convenio)
into STRICT	cd_pessoa_fisica_externo_w,
	cd_usuario_convenio_w	
from	pf_codigo_externo
where 	cd_pessoa_fisica 		= cd_pessoa_fisica_p
and	cd_pessoa_fisica_externo	= cd_pessoa_fisica_externo_p
and	cd_usuario_convenio		= cd_usuario_convenio_p
and (coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w);


if (coalesce(cd_pessoa_fisica_externo_w::text, '') = '') and (coalesce(cd_usuario_convenio_w::text, '') = '') then

	insert into	pf_codigo_externo(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_codigo_externo,
			cd_pessoa_fisica,
			cd_pessoa_fisica_externo,
			cd_usuario_convenio,
			cd_estabelecimento
			)
		values (
			nextval('pf_codigo_externo_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'GPL',
			cd_pessoa_fisica_p,
			cd_pessoa_fisica_externo_p,
			cd_usuario_convenio_p,
			cd_estabelecimento_w
			);
end if;
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
