CREATE OR REPLACE PROCEDURE gera_propaci_lab (nr_prescricao_p bigint, nr_seq_prescr_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;

C01 CURSOR FOR 
SELECT a.nr_sequencia	 
from  prescr_procedimento a 
where not exists (SELECT z.nr_sequencia 
			 from procedimento_paciente z 
			 where z.nr_prescricao = nr_prescricao_p 
			  and z.nr_sequencia_prescricao = a.nr_sequencia)	 
 and a.nr_prescricao		= nr_prescricao_p 
 and ((a.nr_sequencia		= nr_seq_prescr_p) or (nr_seq_prescr_p		= 0));


BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 	into nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	CALL Gerar_Proc_Pac_Prescricao(nr_prescricao_p, nr_sequencia_w, 0, 0, nm_usuario_p, null, null,null);
	update prescr_procedimento 
	set dt_baixa  = NULL, 
	  cd_motivo_baixa = 0 
	where nr_prescricao = nr_prescricao_p 
	 and nr_sequencia = nr_sequencia_w;
END LOOP;
CLOSE C01;
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
