	coalesce(b.qt_decimais, coalesce(c.qt_decimais, qt_dec_exame_w)) qt_decimais,
	c.ds_regra,
	c.ds_texto_padrao,
	A.nr_coluna + 10,
	A.nr_linha + 10
FROM exame_lab_format d, exame_laboratorio c, exame_lab_format_item a
LEFT OUTER JOIN exame_lab_material b ON (A.NR_SEQ_EXAME = B.NR_SEQ_EXAME AND nr_seq_material_p = B.NR_SEQ_MATERIAL)
