end if;

if (coalesce(nr_seq_formato_w,0) > 0) and (coalesce(dt_aprovacao_w::text, '') = '') then
	begin

	begin
	select coalesce(max(nr_sequencia),0)
	into STRICT nr_sequencia_w
	from exame_lab_result_item
	where nr_seq_resultado = nr_seq_resultado_p;
	exception
		when others then
