							round(Obter_Hora_Entre_datas(obter_data_nascto_pf(cd_pessoa_fisica_w),dt_coleta_w))
						into STRICT 	qt_dias_coleta_w,
							qt_horas_coleta_w
						;

					end if;

					if (dt_transf_w IS NOT NULL AND dt_transf_w::text <> '') then

						select 	obter_dias_entre_datas(dt_transf_w,dt_coleta_w),
							round(Obter_Hora_Entre_datas(dt_transf_w,dt_coleta_w))
						into STRICT 	qt_dias_transf_w,
							qt_horas_transf_w
						;

					end if;


					select	max(ie_classif_lote_ent)
					into STRICT	ie_classif_lote_ent_w
					from	atendimento_paciente
					where	nr_atendimento = nr_atendimento_w;

					select	Obter_Atecaco_atendimento(nr_atendimento_w)
					into STRICT	nr_seq_atecaco_w
					;

					if (coalesce(nr_seq_atecaco_w,0) <> 0) then

						select	max(cd_convenio)
						into STRICT	lote_cd_convenio_w
						from	atend_categoria_convenio
						where	nr_seq_interno = nr_seq_atecaco_w;

					end if;

					select 	max(cd_material)
					into STRICT	lote_medic_uso_w
					from	paciente_medic_uso
					where	cd_pessoa_fisica = lote_pessoa_fisica_w;

					select	max(cd_municipio_ibge)
					into STRICT	lote_cd_municipio_w
					from	pessoa_fisica
					where	cd_pessoa_fisica = lote_pessoa_fisica_w;

					select	max(a.ie_tipo_coleta)
					into STRICT	lote_ie_tipo_coleta_w
					from	lote_ent_sec_ficha_exam a,
							lote_ent_sec_ficha b
					where	a.nr_seq_exame = nr_seq_exame_w
					and		a.nr_seq_ficha = b.nr_sequencia
					and		b.nr_prescricao = nr_prescricao_p;

					select	coalesce(max(a.nr_sequencia),0),
							coalesce(max(b.nr_sequencia),null)
					into STRICT	nr_seq_met_reag_w,
							nr_seq_reagente_w
					FROM lab_reagente b, metodo_reagente_lab a
LEFT OUTER JOIN lab_reagente_estab c ON (a.nr_seq_reagente = c.nr_seq_reagente)
WHERE a.nr_seq_metodo = nr_seq_metodo_w and a.nr_seq_reagente = b.nr_sequencia  and coalesce(c.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;

                    SELECT * FROM define_reference_result_values(	nr_prescricao_p, nr_seq_prescr_p, nr_seq_resultado_p, null, null, null, nr_seq_exame_p, nr_seq_material_p, nr_seq_metodo_w, qt_minima_w, qt_maxima_w, pr_minimo_w, pr_maximo_w, ds_referencia_w, ie_consiste_w, nr_sequencia_padrao_w, ds_msg_criterio_w, ie_acao_criterio_lote_w, nr_sequencia_criterio_w
                                                    ) INTO STRICT qt_minima_w, qt_maxima_w, pr_minimo_w, pr_maximo_w, ds_referencia_w, ie_consiste_w, nr_sequencia_padrao_w, ds_msg_criterio_w, ie_acao_criterio_lote_w, nr_sequencia_criterio_w
;


					select 	max(a.ds_result_sugerido)
                    into STRICT    ds_result_sugerido_w
					from    lab_valor_padrao_criterio a
					where	a.nr_sequencia = nr_sequencia_criterio_w;

				end if;
				if (nr_sequencia_padrao_w = 0) then
					select	coalesce(max(a.nr_sequencia),0),
							coalesce(max(b.nr_sequencia),null)
					into STRICT	nr_seq_met_reag_w,
							nr_seq_reagente_w
					FROM lab_reagente b, metodo_reagente_lab a
LEFT OUTER JOIN lab_reagente_estab c ON (a.nr_seq_reagente = c.nr_seq_reagente)
WHERE a.nr_seq_metodo = nr_seq_metodo_w and a.nr_seq_reagente = b.nr_sequencia  and coalesce(c.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;

				--931430
				  if coalesce(nr_seq_reagente_w,0) = 0 then
				    select	coalesce(max(l.nr_sequencia),null), coalesce(max(nr_seq_metodo_w),0)
				    into STRICT	nr_seq_reagente_w, nr_seq_met_reag_w
				    FROM lab_reagente l, lab_reagente_regra r
LEFT OUTER JOIN lab_reagente_estab c ON (r.nr_seq_reagente = c.nr_seq_reagente)
WHERE l.nr_sequencia	= r.nr_seq_reagente and r.dt_inicio_vigencia	<= clock_timestamp() and coalesce(r.dt_fim_vigencia,clock_timestamp())	>= clock_timestamp() and coalesce(r.nr_seq_metodo,nr_seq_metodo_w)		= nr_seq_metodo_w and r.nr_seq_exame		= nr_seq_exame_w and r.ie_situacao 		= 'a'  and coalesce(c.cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;
				  end if;

					select 	qt_minima,
							qt_maxima,
						qt_percent_min,
						qt_percent_max,
						ds_observacao,
						CASE WHEN ie_tipo_valor=3 THEN 'N'  ELSE 'S' END
					into STRICT	qt_minima_w,
						qt_maxima_w,
						pr_minimo_w,
						pr_maximo_w,
						ds_referencia_w,
						ie_consiste_w
					from (
					SELECT qt_minima,
						 qt_maxima,
						 qt_percent_min,
						 qt_percent_max,
						 ds_observacao,
						 ie_tipo_valor,
						 nr_sequencia
					from exame_lab_padrao
					where ((ie_sexo = ie_sexo_p) or (ie_sexo = '0'))
					  and nr_seq_exame = nr_seq_exame_w
					  and coalesce(nr_seq_material,nr_seq_material_p) = nr_seq_material_p
					  and coalesce(nr_seq_metodo, nr_seq_metodo_w) = nr_seq_metodo_w
					  and coalesce(nr_seq_met_reag,nr_seq_met_reag_w)	= nr_seq_met_reag_w
					  and (((trunc((qt_dias_w / 365.25),qt_casas_decimais_dias_w) between qt_idade_min and qt_idade_max) and (ie_periodo = 'A')) or
						   ((trunc(((qt_dias_w / 365.25) * 12), qt_casas_decimais_dias_w) between qt_idade_min and qt_idade_max) and (ie_periodo = 'M')) or
						(qt_dias_w between qt_idade_min and qt_idade_max AND ie_periodo = 'D') or
						(qt_horas_w between qt_idade_min and qt_idade_max AND ie_periodo = 'H'))
					  and ie_tipo_valor in (0,3)
					  and coalesce(ie_situacao,'A') = 'A'
					order by coalesce(nr_seq_material, 9999999999), coalesce(nr_seq_metodo, 9999999999), ie_sexo,
		CASE WHEN ie_periodo='D' THEN 1 WHEN ie_periodo='M' THEN 2  ELSE 3 END , coalesce(nr_seq_prioridade,1)) alias25 LIMIT 1;

				end if;

				exception
					when others then

						CALL gravar_log_lab_pragma(45685,obter_desc_expressao(289370)||' '||obter_desc_expressao(325814)||nr_prescricao_p||' '||obter_desc_expressao(621805)||sqlerrm,'TASY',nr_prescricao_p,'LABPADRAO');
						qt_minima_w 	:= null;
						qt_maxima_w 	:= null;
						pr_minimo_w 	:= null;
						pr_maximo_w 	:= null;
						ds_referencia_w	:= null;
						ie_consiste_w	:= 'N';
				end;

			select	MAX(nr_sequencia),
					MAX(ds_resultado),
					MAX(qt_resultado),
					MAX(pr_resultado),
					MAX(ds_referencia_ext),
					max(ds_referencia),
					max(pr_minimo),
					max(pr_maximo),
					max(qt_minima),
					max(qt_maxima),
					MAX(ds_unidade_medida),
					MAX(nr_seq_unid_med)
			into STRICT	qt_exames_result_w,
					ds_resultado_ww,
					qt_resultado_ww,
					pr_resultado_ww,
					ds_referencia_ext_w,
					ds_referencia_atual_w,
					pr_minimo_atual_w,
					pr_maximo_atual_w,
					qt_minima_atual_w,
					qt_maxima_atual_w,
					ds_unidade_medida_atual_w,
					nr_seq_unid_med_atual_w
			from	exame_lab_result_item
			where	nr_seq_resultado	= nr_seq_resultado_p
			  and	nr_seq_prescr		= nr_seq_prescr_p
			  and	nr_seq_exame		= nr_seq_exame_w;

			if (ds_referencia_ext_w IS NOT NULL AND ds_referencia_ext_w::text <> '') then
				ds_referencia_w := ds_referencia_ext_w;
			end if;

			if (ie_atual_ref_ger_laudo_w = 'N') then

				if (coalesce(ds_referencia_w::text, '') = '') then
					ds_referencia_w := ds_referencia_atual_w;
				end if;
				if (coalesce(pr_minimo_w::text, '') = '') then
					pr_minimo_w := pr_minimo_atual_w;
				end if;
				if (coalesce(pr_maximo_w::text, '') = '') then
					pr_maximo_w := pr_maximo_atual_w;
				end if;
				if (coalesce(qt_minima_w::text, '') = '') then
					qt_minima_w := qt_minima_atual_w;
				end if;
				if (coalesce(qt_maxima_w::text, '') = '') then
					qt_maxima_w := qt_maxima_atual_w;
				end if;

			elsif (ie_atual_ref_ger_laudo_w = 'T') then -- nao sobreescreve a referencia, somente se nao houver um valor anterior,(Para os exames que nao recebem valor da integracao)
				if (ds_referencia_atual_w IS NOT NULL AND ds_referencia_atual_w::text <> '') then
					ds_referencia_w := ds_referencia_atual_w;
				end if;
				if (pr_minimo_atual_w IS NOT NULL AND pr_minimo_atual_w::text <> '') then
					pr_minimo_w := pr_minimo_atual_w;
				end if;
				if (pr_maximo_atual_w IS NOT NULL AND pr_maximo_atual_w::text <> '') then
					pr_maximo_w := pr_maximo_atual_w;
				end if;
				if (qt_minima_atual_w IS NOT NULL AND qt_minima_atual_w::text <> '') then
					qt_minima_w := qt_minima_atual_w;
				end if;
				if (qt_maxima_atual_w IS NOT NULL AND qt_maxima_atual_w::text <> '') then
					qt_maxima_w := qt_maxima_atual_w;
				end if;

			end if;

			if (ie_atual_unid_ger_laudo_w = 'N') then
				if (ds_unidade_medida_atual_w IS NOT NULL AND ds_unidade_medida_atual_w::text <> '') then
					ds_unidade_medida_w := ds_unidade_medida_atual_w;
				end if;
				if (nr_seq_unid_med_atual_w IS NOT NULL AND nr_seq_unid_med_atual_w::text <> '') then
					nr_seq_unid_med_w := nr_seq_unid_med_atual_w;
				end if;
			end if;

			ds_resultado_w	:= coalesce(lab_obter_valor_padrao(ds_regra_w),ds_texto_padrao_w);

			if (qt_exames_result_w > 0) then

				update exame_lab_result_item
				set pr_minimo = pr_minimo_w,
				    pr_maximo = pr_maximo_w,
				    qt_minima = qt_minima_w,
				    qt_maxima = qt_maxima_w,
				    ds_referencia = ds_referencia_w,
				    ie_consiste = ie_consiste_w,
				    ds_unidade_medida = coalesce(ds_unidade_medida_w,' '),
				    nr_seq_unid_med = nr_seq_unid_med_w,
				    qt_decimais = qt_decimais_w,
				    ds_regra = ds_regra_w,
					nr_seq_reagente = coalesce(nr_seq_reagente_w,nr_seq_reagente),
					ie_acao_criterio_lote = ie_acao_criterio_w,
					ds_mensagem_criterio = ds_msg_criterio_w,
					ds_result_sug_crit = ds_result_sugerido_w
				where nr_seq_resultado	= nr_seq_resultado_p
				  and nr_seq_prescr	= nr_seq_prescr_p
				  and nr_seq_exame	= nr_seq_exame_w;

				if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then
					if (ie_formato_result_w = 'V') or (ie_formato_result_w = 'VP' and ie_campo_calculo_w = 'P') then

						begin
						if (coalesce(qt_resultado_ww::text, '') = '') then
							update exame_lab_result_item
							set	qt_resultado = ds_resultado_w
							where nr_seq_resultado	= nr_seq_resultado_p
							  and nr_seq_prescr	= nr_seq_prescr_p
							  and nr_seq_exame	= nr_seq_exame_w;
						end if;
						 exception
						 when others then
						 	--O exame #@DS_EXAME#@ esta cadastrado com formato do tipo numerico (valor ou percentual), porem a regra ou texto padrao so descritivos!
							CALL wheb_mensagem_pck.exibir_mensagem_abort(196315,'DS_EXAME='||obter_desc_exame(nr_seq_exame_w));
						 end;

					elsif (ie_formato_result_w = 'P') or (ie_formato_result_w = 'VP' and ie_campo_calculo_w = 'V') then
						begin
						if (coalesce(pr_resultado_ww::text, '') = '') then
							update exame_lab_result_item
							set	pr_resultado = ds_resultado_w
							where nr_seq_resultado	= nr_seq_resultado_p
							  and nr_seq_prescr	= nr_seq_prescr_p
							  and nr_seq_exame	= nr_seq_exame_w;
						end if;
						exception
						when others then
							CALL wheb_mensagem_pck.exibir_mensagem_abort(196315,'DS_EXAME='||obter_desc_exame(nr_seq_exame_w));
						end;
					else
						if (coalesce(ds_resultado_ww::text, '') = '') then
							update exame_lab_result_item
							set	ds_resultado = ds_resultado_w
							where nr_seq_resultado	= nr_seq_resultado_p
							  and nr_seq_prescr	= nr_seq_prescr_p
							  and nr_seq_exame	= nr_seq_exame_w;
						end if;
					end if;
				end if;
			else
				select 	obter_metodo_regra_item(nr_prescricao_p,nr_seq_exame_w)
				into STRICT	nr_seq_metodo_exame_w
				;



				qt_resultado_w := 0;
				pr_resultado_w := 0;

				if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then
					if (ie_formato_result_w = 'V') or (ie_formato_result_w = 'VP' and ie_campo_calculo_w = 'P') then
						qt_resultado_w	:= ds_resultado_w;
						ds_resultado_w	:= '';
					elsif (ie_formato_result_w = 'P') or (ie_formato_result_w = 'VP' and ie_campo_calculo_w = 'V') then
						pr_resultado_w	:= ds_resultado_w;
						ds_resultado_w	:= '';
					end if;
				end if;

				if (ie_formato_pai_w = 'CV') and (nr_seq_exame_w = -1) then
					select coalesce(max(qt_volume),0)
					into STRICT qt_resultado_w
					from prescr_proc_material
					where nr_prescricao = nr_prescricao_p
					  and nr_seq_material = nr_seq_material_p;
				end if;

				if (ie_gerar_resultado_padrao_w = 'S') then
					select	max(e.ds_valor)
					into STRICT	ds_resultado_w
					from	exame_lab_valor e
					where	e.nr_seq_exame = nr_seq_exame_w
					and	e.ie_padrao_resultado = 'S';
				end if;

				nr_sequencia_w := nr_sequencia_w + 1;
				insert into exame_lab_result_item(nr_seq_resultado, nr_sequencia, nr_seq_exame, dt_atualizacao,
				 nm_usuario, qt_resultado, ds_resultado, nr_seq_metodo,
				 nr_seq_material, pr_resultado, ie_status, dt_aprovacao,
				 nm_usuario_aprovacao, nr_seq_prescr,pr_minimo,pr_maximo,
				 qt_minima,qt_maxima,ds_referencia,ds_unidade_medida, nr_seq_unid_med,
				qt_decimais, ds_regra, ie_consiste,	nr_seq_reagente, nr_seq_metodo_exame,
				ie_acao_criterio_lote, ds_mensagem_criterio, ds_result_sug_crit)
				values (nr_seq_resultado_p,
		  		 nr_sequencia_w,
			  	 nr_seq_exame_w,
			  	 clock_timestamp(),
			  	 nm_usuario_p,
		  		 CASE WHEN ie_formato_result_w='V' THEN qt_resultado_w WHEN ie_formato_result_w='VP' THEN qt_resultado_w WHEN ie_formato_result_w='CV' THEN qt_resultado_w END ,
				 ds_resultado_w, null, null,
			  	 CASE WHEN ie_formato_result_w='P' THEN pr_resultado_w WHEN ie_formato_result_w='VP' THEN pr_resultado_w END , '', null, '',
				 nr_seq_prescr_p,pr_minimo_w,pr_maximo_w,
				 qt_minima_w,qt_maxima_w,ds_referencia_w,coalesce(ds_unidade_medida_w,' '), nr_seq_unid_med_w,
				 qt_decimais_w, ds_regra_w, ie_consiste_w, nr_seq_reagente_w, nr_seq_metodo_exame_w,
				 ie_acao_criterio_w, ds_msg_criterio_w, ds_result_sugerido_w);
			end if;

			end;

		else
			exit;
		end if;
	END LOOP;
	CLOSE C010;
	end;
end if;

begin

ie_alterar_status_w	:= 'S';
--Integracao DataInnovations
if (wheb_usuario_pck.is_evento_ativo('73') = 'S') or (wheb_usuario_pck.is_evento_ativo('74') = 'S') or (wheb_usuario_pck.is_evento_ativo('84') = 'S') or (wheb_usuario_pck.is_evento_ativo('222') = 'S') then

	select	max(a.nr_seq_exame)
	into STRICT	nr_seq_exame_w
	from	prescr_procedimento a
	where	a.nr_prescricao	= nr_prescricao_p
	and 	a.nr_sequencia	= nr_seq_prescr_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_curva_w
	from	exame_laboratorio a
	where	a.nr_seq_superior = nr_seq_exame_w
	and	(a.nr_ordem_amostra IS NOT NULL AND a.nr_ordem_amostra::text <> '');

	if (ie_curva_w = 'S') then

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_alterar_status_w
		from	prescr_proc_mat_item a
		where	a.nr_prescricao = nr_prescricao_p
		and	a.nr_seq_prescr = nr_seq_prescr_p
		and	a.ie_status < CASE WHEN nm_usuario_p='ADMINIST' THEN  35  ELSE 30 END;

	end if;
end if;

select CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
  into STRICT ie_alterar_status_w
  from prescr_medica
 where nr_prescricao = nr_prescricao_p
   and (NR_SEQ_FICHA_LOTE IS NOT NULL AND NR_SEQ_FICHA_LOTE::text <> '');

if (ie_alterar_status_w = 'S') then
	if (ie_amostra_diferente_w = 'S') then
		update 	prescr_procedimento a
		set	a.ie_status_atend = CASE WHEN nm_usuario_p='ADMINIST' THEN  35  ELSE 30 END ,
			a.ie_amostra = 'S',
			a.nm_usuario = nm_usuario_p,
			a.dt_atualizacao = clock_timestamp()
		where 	a.nr_prescricao	= nr_prescricao_p
		and 	a.nr_sequencia	= nr_seq_prescr_p
		and 	a.ie_status_atend < 30
		and not exists (SELECT 	1
				from 	prescr_proc_mat_item b
				where 	b.nr_prescricao = a.nr_prescricao
				and	b.nr_seq_prescr = a.nr_sequencia
				and	b.ie_status <= 20);
	else
		update 	prescr_procedimento
		set	ie_status_atend = CASE WHEN nm_usuario_p='ADMINIST' THEN  35  ELSE 30 END ,
			ie_amostra = 'S',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	nr_prescricao	= nr_prescricao_p
		  and 	nr_sequencia	= nr_seq_prescr_p
		  and 	ie_status_atend < 30;
	end if;
end if;

exception
	when others then
		--Erro ao atualizar a prescricao
		CALL gravar_log_lab_pragma(34,obter_desc_expressao(504171)||nr_seq_prescr_p||' - '||substr(sqlerrm,1,2000),nm_usuario_p,nr_prescricao_p);
		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

		CALL wheb_mensagem_pck.exibir_mensagem_abort(196316);
end;


if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
END;
$body$
