-- REVOKE ALL ON PROCEDURE gera_resultado_lab ( nr_seq_resultado_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_exame_p bigint, nr_seq_material_p bigint, nr_idade_p bigint, ie_sexo_p text, nm_usuario_p text, nr_seq_formato_p bigint default null) FROM PUBLIC;

