CREATE OR REPLACE PROCEDURE gera_solic_desconto_convenio ( cd_pessoa_fisica_p text, cd_pessoa_juridica_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ret_item_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_ret_item_w
from	CONVENIO_RET_ITEM_DESC
where	nr_seq_ret_item = nr_sequencia_p;

if ( nr_seq_ret_item_w > 0) then
	begin

		update   CONVENIO_RET_ITEM_DESC
		set     cd_pessoa_fisica    =  cd_pessoa_fisica_p,
			cd_cgc              = cd_pessoa_juridica_p,
			nm_usuario	    = nm_usuario_p
		where   nr_sequencia        = nr_seq_ret_item_w;

	end;
else
	begin

		insert into 	CONVENIO_RET_ITEM_DESC(nr_sequencia, dt_atualizacao, nm_usuario, nr_seq_ret_item, cd_pessoa_fisica, cd_cgc )
		values (nextval('convenio_ret_item_desc_seq'), clock_timestamp(), nm_usuario_p, nr_sequencia_p, cd_pessoa_fisica_p, cd_pessoa_juridica_p );

	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
