CREATE OR REPLACE PROCEDURE gera_usu_conv_ops_agepac (nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, carteiras_selec_p INOUT text) AS $body$
DECLARE


cd_usuario_plano_w	varchar(255);
dt_validade_w		timestamp;
cd_pessoa_fisica_w	varchar(10);
dt_agenda_w		timestamp;


BEGIN

select  dt_agenda,
		cd_pessoa_fisica
into STRICT	dt_agenda_w,
		cd_pessoa_fisica_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

SELECT * FROM pls_obter_usuario_plano(cd_pessoa_fisica_w, dt_agenda_w, cd_estabelecimento_p, cd_usuario_plano_w, dt_validade_w) INTO STRICT cd_usuario_plano_w, dt_validade_w;

if (coalesce(cd_usuario_plano_w,'X') <> 'X') and (position(',' in cd_usuario_plano_w) = 0) then

	update	agenda_paciente
	set	cd_usuario_convenio	= cd_usuario_plano_w,
		dt_validade_carteira	= dt_validade_w
	where	nr_sequencia		= nr_seq_agenda_p;

	select	null
	into STRICT	carteiras_selec_p
	;

else

	select	cd_usuario_plano_w
	into STRICT	carteiras_selec_p
	;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
