CREATE OR REPLACE PROCEDURE gera_validacao_digital_pessoa (cd_pessoa_biometria_p text, nm_usuario_p text, cd_pessoa_fisica_p INOUT text, nm_paciente_p INOUT text) AS $body$
DECLARE



cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
nm_paciente_w			pessoa_fisica.nm_pessoa_fisica%type;


BEGIN

if (cd_pessoa_biometria_p IS NOT NULL AND cd_pessoa_biometria_p::text <> '')  then
	select 	max(b.cd_pessoa_fisica),
		substr(obter_nome_pf(max(b.cd_pessoa_fisica)),1,255)
	into STRICT	cd_pessoa_fisica_w,
		nm_paciente_w
	from	pessoa_fisica_biometria b
	where	b.nr_sequencia = cd_pessoa_biometria_p;
end if;

nm_paciente_p := nm_paciente_w;
cd_pessoa_fisica_p := cd_pessoa_fisica_w;

end;
$body$
