CREATE OR REPLACE PROCEDURE gera_w_carga_material ( nr_seq_estrutura_p bigint, cd_material_p bigint, nm_usuario_p text) AS $body$
DECLARE

ie_existe_w	smallint;

BEGIN

select	count(1)
into STRICT	ie_existe_w
from	material
where	cd_material = cd_material_p;

if (ie_existe_w > 0) then
	begin
	insert into mat_estrutura_cadastro(nr_sequencia,
		nr_seq_estrutura,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_material)
	values ( nextval('mat_estrutura_cadastro_seq'),
		nr_seq_estrutura_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
    nm_usuario_p,
		cd_material_p);
    end;
	commit;
end if;

end;
$body$
