CREATE OR REPLACE PROCEDURE gerenciar_filtros ( cd_funcao_p bigint, ds_filtro_p text, nm_usuario_p text, ie_opcao_p text, cd_perfil_p bigint default null, nr_seq_filtro_p bigint default null, VL_CAMPO_P text default '', DS_CAMPO_P text default null, nm_filtro_p text default null) AS $body$
BEGIN

if (ie_opcao_p = 'D') then
	begin
		delete from funcao_filtro
		where nm_usuario = nm_usuario_p
		and cd_funcao = cd_funcao_p
		and ds_filtro like '%' || ds_filtro_p || '%';
	end;
end if;

if (ie_opcao_p = 'I') then
	begin

		insert into FUNCAO_FILTRO(
			NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
			DS_FILTRO,
			IE_TIPO_CAMPO,
			DS_CAMPO,
			CD_FUNCAO,
			IE_TIPO_FILTRO,
			VL_CAMPO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			NR_SEQ_FILTRO,
			CD_PERFIL,
			NM_FILTRO)
		values (
			nextval('funcao_filtro_seq'),
			clock_timestamp(),
			nm_usuario_p,
			ds_filtro_p,
			'N',
			VL_CAMPO_P,
			cd_funcao_p,
			'D',
			DS_CAMPO_P,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_filtro_p,
			cd_perfil_p,
			nm_filtro_p);
	end;
end if;

commit;

end;
