SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerencia_w_tit_pagar_uni ( nr_titulo_p bigint, ie_comportamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_w  			titulo_pagar.nr_titulo%type;
vl_saldo_titulo_w 		titulo_pagar.vl_saldo_titulo%type;
dt_emissao_w    		titulo_pagar.dt_emissao%type;
dt_vencimento_atual_w		titulo_pagar.dt_vencimento_atual%type;
vl_juros_bordero_w 		titulo_pagar.vl_juros_bordero%type;
vl_multa_bordero_w 		titulo_pagar.vl_multa_bordero%type;
vl_dia_antecipacao_w		titulo_pagar.vl_dia_antecipacao%type;
nr_seq_nota_fiscal_w		titulo_pagar.nr_seq_nota_fiscal%type;

/*

IE_COMPORTAMENTE_P
	1 - Inserir dados na tabela W_TIT_PAGAR_UNI
	2 - Deletar todo os registros da tabela W_TIT_PAGAR_UNI
*/
BEGIN

	if (ie_comportamento_p = 1) then

		select  	max(a.nr_titulo),
				max(a.vl_saldo_titulo),
				max(a.dt_emissao),
				max(a.dt_vencimento_atual),
				max(a.vl_juros_bordero),
				max(a.vl_multa_bordero),
				max(a.vl_dia_antecipacao),
				max(a.nr_seq_nota_fiscal)
		into STRICT
				nr_titulo_w,
				vl_saldo_titulo_w,
				dt_emissao_w,
				dt_vencimento_atual_w,
				vl_juros_bordero_w,
				vl_multa_bordero_w,
				vl_dia_antecipacao_w,
				nr_seq_nota_fiscal_w
		from    	titulo_pagar  a
		where   	nr_titulo  =  nr_titulo_p;

		insert into W_TIT_PAGAR_UNI(NR_TITULO,
				VL_SALDO_TITULO,
				DT_EMISSAO,
				DT_VENCIMENTO_ATUAL,
				VL_JUROS_BORDERO,
				VL_MULTA_BORDERO,
				VL_DIA_ANTECIPACAO,
				NR_SEQ_NOTA_FISCAL,
				NM_USUARIO)
		values (	nr_titulo_w,
				vl_saldo_titulo_w,
				dt_emissao_w,
				dt_vencimento_atual_w,
				vl_juros_bordero_w,
				vl_multa_bordero_w,
				vl_dia_antecipacao_w,
				nr_seq_nota_fiscal_w,
				nm_usuario_p);

	end if;


	if (ie_comportamento_p = 2) then
		delete from w_tit_pagar_uni a  where  a.nm_usuario = nm_usuario_p;
	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
